// ==UserScript==
// @name         DNS List Clean
// @description  Remove product cards by filter
// @match        https://www.dns-shop.ru/catalog/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.1.70842958
// @icon         https://www.google.com/s2/favicons?sz=64&domain=dns-shop.ru
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function hideElement(element){setElementDisplay(element,"none")}function showElement(element,display="block"){setElementDisplay(element,display)}function showHideElement(element,conditionToHide,display="block"){conditionToHide?hideElement(element):showElement(element,display)}function setElementDisplay(element,display){element.style.display=display}function createTextInput(inputOnChange,inputStyle,inputValue){const input=createInput("text",inputOnChange,inputStyle);input.value=inputValue;return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function createCheckboxInput(inputOnChange,inputStyle,isChecked){const input=createInput("checkbox",inputOnChange,inputStyle);input.checked=isChecked;return input}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);if(inputOnChange){input.addEventListener("keyup",inputOnChange);input.addEventListener("change",inputOnChange)}style&&(input.style=style);return input}function createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function getInputValueFromEvent(event){const{target}=event;const{type}=target;switch(type){case"text":return`"${target.value}"`;case"number":return target.value;case"checkbox":return target.checked;default:console.log(`Unknown input type: ${type}`);return null}}class InputValueBase{constructor(value,onChange){this.value=value;this.onChange=onChange}onChangeIfDefined=()=>{this.onChange&&this.onChange()}}function parseValue(value){return value===""?null:JSON.parse(value)}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue){const localStorageItem=storage.getItem(key);return localStorageItem===null?defaultValue:parseValue(localStorageItem)}class StorageValue extends InputValueBase{constructor(storageKey,defaultValue=null,onChange=null){super(getStorageValueOrDefault(storageKey,defaultValue),onChange);this.storageKey=storageKey}updateValueFromEvent=event=>{const newValue=getInputValueFromEvent(event);const newParsedValue=parseValue(newValue);if(this.value===newParsedValue)return;storage.setItem(this.storageKey,newValue);this.value=newParsedValue;this.onChangeIfDefined()}}


function createFilterControlText(titleText,inputValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createTextInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value);filterControl.append(input);return filterControl}function createFilterControlNumber(titleText,inputValue,inputStep,inputMinValue,inputMaxValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createNumberInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createFilterControlCheckbox(titleText,inputValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createCheckboxInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value);filterControl.append(input);return filterControl}function createNameFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlText("Содержит: ",inputValue,controlStyle,inputStyle)}function createMinRatingFilterControl(inputValue,controlStyle=null,inputStyle=null,inputStep=.1){return createFilterControlNumber("Мин. рейтинг: ",inputValue,inputStep,3,5,controlStyle,inputStyle)}function createMinReviewsFilterControl(inputValue,controlStyle=null,inputStyle=null){return createReviewsFilterControl("Мин. отзывов: ",inputValue,controlStyle,inputStyle)}function createReviewsFilterControl(titleText,inputValue,controlStyle=null,inputStyle=null){return createFilterControlNumber(titleText,inputValue,"1","1","999999",controlStyle,inputStyle)}function createEnabledFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlCheckbox("Вкл: ",inputValue,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,force=false,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer){if(!force)return;filtersContainer.remove()}filtersContainer=createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}function isNotMatchTextFilter(parameterValue,filter){return!isMatchTextFilter(parameterValue,filter)}function isMatchTextFilter(parameterValue,filter){if(!filter.value)return true;const comparedString=parameterValue.toLowerCase();const searchStrings=filter.value.toLowerCase().split(",").map((searchString=>searchString.trim()));const{include:includeSearchStrings,notInclude:notIncludeSearchStrings}=searchStrings.reduce(((result,searchString)=>{if(searchString.startsWith("!")){const notIncludeSearchString=searchString.substring(1);notIncludeSearchString.length&&result.notInclude.push(notIncludeSearchString)}else result.include.push(searchString);return result}),{include:[],notInclude:[]});return includeSearchStrings.every((searchString=>comparedString.includes(searchString)))&&notIncludeSearchStrings.every((searchString=>!comparedString.includes(searchString)))}function isLessThanFilter(parameterValue,filter){return filter.value&&parameterValue<filter.value}function string_removeNonNumber(stringValue){return stringValue.replace(/[^\d.,-]/g,"")}


const PRODUCTS_PAGE_LIST_SELECTOR=".products-page__list";const CATEGORY_NAME=getCategoryName();function getCategoryName(){const pathElements=getPathElements();const categoryName=pathElements[3]??"common";return categoryName}function getPathElements(){const{pathname}=window.location;return pathname.split("/")}const nameFilter=new StorageValue(`${CATEGORY_NAME}-name-filter`,null,cleanList);const minReviewsFilter=new StorageValue(`${CATEGORY_NAME}-min-reviews-filter`,null,cleanList);const minRatingFilter=new StorageValue(`${CATEGORY_NAME}-min-rating-filter`,4.5,cleanList);const filterEnabled=new StorageValue(`${CATEGORY_NAME}-filter-enabled`,true,cleanList);const productsPageList=getFirstElement(PRODUCTS_PAGE_LIST_SELECTOR);productsPageList&&initListClean();function initListClean(){const topFilters=getFirstElement(".top-filters",productsPageList,true);const productList=getFirstElement(".catalog-products",productsPageList,true);appendFilterControlsIfNeeded(topFilters,appendFiltersContainer);new MutationObserver(cleanList).observe(productList,{childList:true,subtree:true})}function appendFiltersContainer(filtersContainer,parentNode){filtersContainer.style="display: flex;"+"grid-gap: 12px;"+"padding-bottom: 12px;";const controlStyle="display: flex;"+"align-items: center;"+"font-size: 15px;";const inputStyle="margin-left: 6px;"+"border: 1px solid #d9d9d9;"+"border-radius: 8px;"+"padding: 7px 14px;"+"font-size: 15px;";const textInputStyle=inputStyle+// eslint-disable-line prefer-template
"width: 180px;";const numberInputStyle=inputStyle+// eslint-disable-line prefer-template
"width: 90px;";const checkboxInputStyle="margin-left: 6px;"+"width: 21px;"+"height: 21px;";const nameFilterDiv=createNameFilterControl(nameFilter,controlStyle,textInputStyle);const minReviewsDiv=createMinReviewsFilterControl(minReviewsFilter,controlStyle,numberInputStyle);const minRatingDiv=createMinRatingFilterControl(minRatingFilter,controlStyle,numberInputStyle,.5);const filterEnabledDiv=createEnabledFilterControl(filterEnabled,controlStyle,checkboxInputStyle);filtersContainer.append(nameFilterDiv,minReviewsDiv,minRatingDiv,filterEnabledDiv);parentNode.append(filtersContainer)}function cleanList(){const productCards=getAllElements(`${PRODUCTS_PAGE_LIST_SELECTOR} .catalog-product`);productCards.forEach((productCard=>{const display=getProductCardDisplay(productCard);if(!filterEnabled.value){showElement(productCard,display);return}const productCardNameWrap=getFirstElement(".catalog-product__name",productCard);const productCardRatingWrap=getFirstElement(".catalog-product__rating",productCard);if(!productCardNameWrap||!productCardRatingWrap){hideElement(productCard);return}const productCardName=productCardNameWrap.innerText;const productCardReviewsNumber=getProductCardReviewsNumber(productCardRatingWrap);const productCardRatingNumber=+productCardRatingWrap.getAttribute("data-rating");const conditionToHide=isNotMatchTextFilter(productCardName,nameFilter)||isLessThanFilter(productCardReviewsNumber,minReviewsFilter)||isLessThanFilter(productCardRatingNumber,minRatingFilter);showHideElement(productCard,conditionToHide,display)}))}function getProductCardDisplay(productCard){if(!productCard.hasAttribute("display")){const{display}=getComputedStyle(productCard);productCard.setAttribute("display",display)}return productCard.getAttribute("display")}function getProductCardReviewsNumber(productCardRatingWrap){const productCardReviewsText=productCardRatingWrap.textContent;let productCardReviewsNumber=+string_removeNonNumber(productCardReviewsText);productCardReviewsText.includes("k")&&(productCardReviewsNumber*=1e3);return productCardReviewsNumber}})();