// ==UserScript==
// @name         Lavka List Clean
// @description  Remove product cards without discount
// @match        https://lavka.yandex.ru/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.6.69997775
// @icon         https://www.google.com/s2/favicons?sz=64&domain=yandex.ru
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function insertAfter(existingNode,newNode){existingNode.parentNode.insertBefore(newNode,existingNode.nextSibling)}function hideElement(element){setElementDisplay(element,"none")}function showElement(element,display="block"){setElementDisplay(element,display)}function showHideElement(element,conditionToHide,display="block"){conditionToHide?hideElement(element):showElement(element,display)}function setElementDisplay(element,display){element.style.display=display}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);inputOnChange&&input.addEventListener("change",inputOnChange);style&&(input.style=style);return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function createCheckboxInput(inputOnChange,inputStyle,isChecked){const input=createInput("checkbox",inputOnChange,inputStyle);input.checked=isChecked;return input}function createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function getInputValueFromEvent(event){const{target}=event;const{type}=target;let inputValue;if(type==="number")inputValue=target.value;else{if(type!=="checkbox"){console.log(`Unknown input type: ${type}`);return null}inputValue=target.checked}return parseValue(inputValue)}class InputValueBase{constructor(value,onChange){this.value=value;this.onChange=onChange}onChangeIfDefined=()=>{this.onChange&&this.onChange()}}function parseValue(value){return value===""?null:JSON.parse(value)}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue){const localStorageItem=storage.getItem(key);return localStorageItem===null?defaultValue:parseValue(localStorageItem)}function setStorageValueFromEvent(event,keyName){const inputValue=getInputValueFromEvent(event);storage.setItem(keyName,inputValue);return inputValue}class StorageValue extends InputValueBase{constructor(storageKey,defaultValue=null,onChange=null){super(getStorageValueOrDefault(storageKey,defaultValue),onChange);this.storageKey=storageKey}updateValueFromEvent=event=>{this.value=setStorageValueFromEvent(event,this.storageKey);this.onChangeIfDefined()}}


function removeNonDigit(stringValue){return stringValue.replace(/\D/g,"")}


function createFilterControlNumber(titleText,inputValue,inputStep,inputMinValue,inputMaxValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createNumberInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createFilterControlCheckbox(titleText,inputValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createCheckboxInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value);filterControl.append(input);return filterControl}function createMinDiscountFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlNumber("Минимальная скидка: ",inputValue,"1","0","100",controlStyle,inputStyle)}function createEnabledFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlCheckbox("Вкл: ",inputValue,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer)return;filtersContainer=createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}function isLessThanFilter(parameterValue,filter){return filter.value&&parameterValue<filter.value}


const minDiscountFilter=new StorageValue("min-discount-filter");const filterEnabled=new StorageValue("filter-enabled",true);const MAIN_CONTENT_SELECTOR="#main-content-id";const PRODUCT_CARD_LINK_SELECTOR='[data-type="product-card-link"]';const mainContent=getFirstElement(MAIN_CONTENT_SELECTOR);setInterval(initListClean,500);function initListClean(){const productCardLinks=getAllElements(PRODUCT_CARD_LINK_SELECTOR);if(productCardLinks.length){appendFilterControlsIfNeeded(mainContent,appendFiltersContainer);cleanList(productCardLinks)}}function appendFiltersContainer(filtersContainer,parentNode){const minDiscountDiv=createMinDiscountFilterControl(minDiscountFilter);const filterEnabledDiv=createEnabledFilterControl(filterEnabled);filtersContainer.append(minDiscountDiv,filterEnabledDiv);insertAfter(parentNode.firstChild,filtersContainer)}function cleanList(productCardLinks){if(minDiscountFilter.value===0)return;productCardLinks.forEach((productCardLink=>{const productCardLinksParent=productCardLink.parentNode;const productCard=productCardLinksParent.parentNode.parentNode;if(!filterEnabled.value){showElement(productCard);return}const promoLabel=getFirstElement("li",productCardLinksParent);if(!promoLabel){hideElement(productCard);return}const promoLabelText=promoLabel.innerText;if(!promoLabelText.includes("%")){hideElement(productCard);return}const discountValue=+removeNonDigit(promoLabelText);const conditionToHide=isLessThanFilter(discountValue,minDiscountFilter);showHideElement(productCard,conditionToHide)}))}})();