// ==UserScript==
// @name         Lavka List Clean
// @description  Remove product cards without discount
// @match        https://lavka.yandex.ru/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.4.69667285
// @icon         https://www.google.com/s2/favicons?sz=64&domain=yandex.ru
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";


function getFirstElement(parentNode,selector,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(parentNode,selector,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function insertAfter(existingNode,newNode){existingNode.parentNode.insertBefore(newNode,existingNode.nextSibling)}function hideElement(element){setElementDisplay(element,"none")}function showElement(element){setElementDisplay(element,"block")}function setElementDisplay(element,display){element.style.display=display}function updateValue(event,keyName,needReload=true){const{target}=event;const{type}=target;let valueToSet;if(type==="number")valueToSet=target.value;else{if(type!=="checkbox"){console.log(`Unknown input type: ${type}`);return null}valueToSet=target.checked}localStorage.setItem(keyName,valueToSet);needReload&&window.location.reload();return valueToSet}function createFilterControlNumber(titleText,inputValue,inputStep,inputMinValue,inputMaxValue,inputOnChange,controlStyle="",inputStyle=""){const filterControl=createDiv(titleText,controlStyle);const input=createBaseInput(inputOnChange,inputStyle);input.type="number";input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;filterControl.append(input);return filterControl}function createBaseInput(inputOnChange,style){const input=document.createElement("input");input.addEventListener("change",inputOnChange);input.style=style;return input}function createDiv(textContent,style=""){const div=document.createElement("div");div.textContent=textContent;div.style=style;return div}


const MIN_DISCOUNT=20;const MIN_DISCOUNT_LOCAL_STORAGE_KEY="minDiscountFilter";const MAIN_CONTENT_SELECTOR="#main-content-id";const PRODUCT_CARD_LINK_SELECTOR='[data-type="product-card-link"]';const FILTERS_CONTAINER_ID="filtersContainer";let minDiscountValue=+(localStorage.getItem(MIN_DISCOUNT_LOCAL_STORAGE_KEY)??MIN_DISCOUNT);const mainContent=getFirstElement(document,MAIN_CONTENT_SELECTOR);setInterval(initListClean,500);function initListClean(){const productCardLinks=getAllElements(document,PRODUCT_CARD_LINK_SELECTOR);if(productCardLinks.length){appendFilterControlsIfNeeded();cleanList(productCardLinks)}}function appendFilterControlsIfNeeded(){const filtersContainer=getFirstElement(mainContent,`#${FILTERS_CONTAINER_ID}`);if(filtersContainer)return;appendFilterControls()}function appendFilterControls(){const filtersContainer=document.createElement("div");filtersContainer.id=FILTERS_CONTAINER_ID;const minDiscountDiv=createFilterControlNumber("Минимальная скидка: ",minDiscountValue,"1","0","100",updateMinDiscountInput);filtersContainer.append(minDiscountDiv);insertAfter(mainContent.firstChild,filtersContainer)}function updateMinDiscountInput(e){minDiscountValue=updateValue(e,MIN_DISCOUNT_LOCAL_STORAGE_KEY,false)}function cleanList(productCardLinks){if(minDiscountValue===0)return;productCardLinks.forEach((productCardLink=>{const productCardLinksParent=productCardLink.parentNode;const productCard=productCardLinksParent.parentNode.parentNode;const promoLabel=getFirstElement(productCardLinksParent,"li");if(!promoLabel){productCard.remove();return}const promoLabelText=promoLabel.innerText;if(!promoLabelText.includes("%")){productCard.remove();return}const discountValue=+promoLabelText.replace(/\D/g,"");discountValue<minDiscountValue?hideElement(productCard):showElement(productCard)}))}})();