// ==UserScript==
// @name         Lavka List Clean
// @description  Remove product cards without discount
// @match        https://lavka.yandex.ru/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.6.69757140
// @icon         https://www.google.com/s2/favicons?sz=64&domain=yandex.ru
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function insertAfter(existingNode,newNode){existingNode.parentNode.insertBefore(newNode,existingNode.nextSibling)}function hideElement(element){setElementDisplay(element,"none")}function showElement(element,display="block"){setElementDisplay(element,display)}function showHideElement(element,conditionToHide,display="block"){conditionToHide?hideElement(element):showElement(element,display)}function setElementDisplay(element,display){element.style.display=display}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);inputOnChange&&input.addEventListener("change",inputOnChange);style&&(input.style=style);return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function createCheckboxInput(inputOnChange,inputStyle,isChecked){const input=createInput("checkbox",inputOnChange,inputStyle);input.checked=isChecked;return input}function createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue){const localStorageItem=storage.getItem(key);return localStorageItem?JSON.parse(localStorageItem):defaultValue}function setStorageValueFromEvent(event,keyName){const{target}=event;const{type}=target;let valueToSet;if(type==="number")valueToSet=target.value;else{if(type!=="checkbox"){console.log(`Unknown input type: ${type}`);return null}valueToSet=target.checked}storage.setItem(keyName,valueToSet);return valueToSet}class StorageValue{constructor(storageKey,defaultValue){this.value=getStorageValueOrDefault(storageKey,defaultValue);this.storageKey=storageKey}updateValueFromEvent=event=>{this.value=setStorageValueFromEvent(event,this.storageKey)}}


function removeNonDigit(stringValue){return stringValue.replace(/\D/g,"")}


function createFilterControlNumber(titleText,storageValue,inputStep,inputMinValue,inputMaxValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createNumberInput(storageValue.updateValueFromEvent,inputStyle,storageValue.value,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createFilterControlCheckbox(titleText,storageValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createCheckboxInput(storageValue.updateValueFromEvent,inputStyle,storageValue.value);filterControl.append(input);return filterControl}function createMinDiscountFilterControl(storageValue,controlStyle=null,inputStyle=null){return createFilterControlNumber("Минимальная скидка: ",storageValue,"1","0","100",controlStyle,inputStyle)}function createEnabledFilterControl(storageValue,controlStyle=null,inputStyle=null){return createFilterControlCheckbox("Вкл: ",storageValue,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer)return;filtersContainer=createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}


const minDiscountFilter=new StorageValue("min-discount-filter",20);const filterEnabled=new StorageValue("filter-enabled",true);const MAIN_CONTENT_SELECTOR="#main-content-id";const PRODUCT_CARD_LINK_SELECTOR='[data-type="product-card-link"]';const mainContent=getFirstElement(MAIN_CONTENT_SELECTOR);setInterval(initListClean,500);function initListClean(){const productCardLinks=getAllElements(PRODUCT_CARD_LINK_SELECTOR);if(productCardLinks.length){appendFilterControlsIfNeeded(mainContent,appendFiltersContainer);cleanList(productCardLinks)}}function appendFiltersContainer(filtersContainer,parentNode){const minDiscountDiv=createMinDiscountFilterControl(minDiscountFilter);const filterEnabledDiv=createEnabledFilterControl(filterEnabled);filtersContainer.append(minDiscountDiv,filterEnabledDiv);insertAfter(parentNode.firstChild,filtersContainer)}function cleanList(productCardLinks){if(minDiscountFilter.value===0)return;productCardLinks.forEach((productCardLink=>{const productCardLinksParent=productCardLink.parentNode;const productCard=productCardLinksParent.parentNode.parentNode;if(!filterEnabled.value){showElement(productCard);return}const promoLabel=getFirstElement("li",productCardLinksParent);if(!promoLabel){hideElement(productCard);return}const promoLabelText=promoLabel.innerText;if(!promoLabelText.includes("%")){hideElement(productCard);return}const discountValue=+removeNonDigit(promoLabelText);showHideElement(productCard,discountValue<minDiscountFilter.value)}))}})();