// ==UserScript==
// @name         Lenta List Clean
// @description  Remove product cards by filter
// @match        https://*.online.lenta.com/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.3.69997775
// @icon         https://www.google.com/s2/favicons?sz=64&domain=lenta.com
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";function removeNonNumber(stringValue){return stringValue.replace(/[^\d.,-]/g,"")}


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function defineElementOpacity(element,conditionToDefine,opacity=.2){conditionToDefine?setElementOpacity(element,opacity):resetElementOpacity(element)}function resetElementOpacity(element){element.style.opacity=1}function setElementOpacity(element,opacity=.2){element.style.opacity=opacity}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);inputOnChange&&input.addEventListener("change",inputOnChange);style&&(input.style=style);return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function createCheckboxInput(inputOnChange,inputStyle,isChecked){const input=createInput("checkbox",inputOnChange,inputStyle);input.checked=isChecked;return input}function createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function getElementInnerNumber(element,cleanText=false,replaceComma=false){let elementText=element.innerText;cleanText&&(elementText=removeNonNumber(elementText));replaceComma&&(elementText=elementText.replace(",","."));const elementNumber=+elementText;return elementNumber}function getInputValueFromEvent(event){const{target}=event;const{type}=target;let inputValue;if(type==="number")inputValue=target.value;else{if(type!=="checkbox"){console.log(`Unknown input type: ${type}`);return null}inputValue=target.checked}return parseValue(inputValue)}class InputValueBase{constructor(value,onChange){this.value=value;this.onChange=onChange}onChangeIfDefined=()=>{this.onChange&&this.onChange()}}function parseValue(value){return value===""?null:JSON.parse(value)}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue){const localStorageItem=storage.getItem(key);return localStorageItem===null?defaultValue:parseValue(localStorageItem)}function setStorageValueFromEvent(event,keyName){const inputValue=getInputValueFromEvent(event);storage.setItem(keyName,inputValue);return inputValue}class StorageValue extends InputValueBase{constructor(storageKey,defaultValue=null,onChange=null){super(getStorageValueOrDefault(storageKey,defaultValue),onChange);this.storageKey=storageKey}updateValueFromEvent=event=>{this.value=setStorageValueFromEvent(event,this.storageKey);this.onChangeIfDefined()}}


function createFilterControlNumber(titleText,inputValue,inputStep,inputMinValue,inputMaxValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createNumberInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createFilterControlCheckbox(titleText,inputValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createCheckboxInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value);filterControl.append(input);return filterControl}function createMinRatingFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlNumber("Минимальный рейтинг: ",inputValue,"0.1","3.0","5.0",controlStyle,inputStyle)}function createNoRatingFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlCheckbox("Без рейтинга: ",inputValue,controlStyle,inputStyle)}function createEnabledFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlCheckbox("Вкл: ",inputValue,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer)return;filtersContainer=createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}function isLessThanFilter(parameterValue,filter){return filter.value&&parameterValue<filter.value}


const CATEGORY_NAME=getCategoryName();const minRatingFilter=new StorageValue(`${CATEGORY_NAME}-min-rating-filter`,4.1);const noRatingFilter=new StorageValue(`${CATEGORY_NAME}-no-rating-filter`,false);const filterEnabled=new StorageValue(`${CATEGORY_NAME}-filter-enabled`,true);const discountEnabled=new StorageValue("discount-enabled",true);const PRODUCT_CARD_LIST_SELECTOR=".catalog-list";const PRODUCT_CARD_SELECTOR=".catalog-grid_new__item";const PRODUCT_CARD_RATING_SELECTOR=".rating-number";const DISCOUNTED_PRICE_ADDED_CLASS="discountedPriceAdded";const CURRENT_DISCOUNT=.25;function getCategoryName(){const{pathname}=window.location;const pathElements=pathname.split("/");const lastPathElement=pathElements.pop();const categoryName=/^\d+$/.test(lastPathElement)?lastPathElement:"common";return categoryName}setInterval(initListClean,100);function initListClean(){const productCardList=getFirstElement(PRODUCT_CARD_LIST_SELECTOR);if(productCardList){appendFilterControlsIfNeeded(productCardList,appendFiltersContainer);cleanList()}}function appendFiltersContainer(filtersContainer,parentNode){filtersContainer.style="display: flex;"+"grid-gap: 15px;"+"margin-left: 10px;";const controlStyle="display: flex;"+"align-items: center;";const numberInputStyle="border: 1px solid #C9C9C9;"+"border-radius: 8px;"+"height: 40px;"+"padding: 0 16px;";const checkboxInputStyle="border: 1px solid #C9C9C9;"+"border-radius: 4px;"+"width: 22px;"+"height: 22px;";const minRatingDiv=createMinRatingFilterControl(minRatingFilter,controlStyle,numberInputStyle);const noRatingDiv=createNoRatingFilterControl(noRatingFilter,controlStyle,checkboxInputStyle);const filterEnabledDiv=createEnabledFilterControl(filterEnabled,controlStyle,checkboxInputStyle);const discountEnabledDiv=createFilterControlCheckbox("Скидка:",discountEnabled,controlStyle,checkboxInputStyle);filtersContainer.append(minRatingDiv,noRatingDiv,filterEnabledDiv,discountEnabledDiv);parentNode.prepend(filtersContainer)}function expandProductCardName(productCard){const productCardName=getFirstElement(".lu-product-card-name_new",productCard);if(!productCardName)return;productCardName.style.display="inline-block";productCardName.style.height="91px"}function cleanList(){const productCards=getAllElements(PRODUCT_CARD_SELECTOR);productCards.forEach((productCard=>{expandProductCardName(productCard);discountEnabled.value&&addDiscountedPriceIfNeeded(productCard);if(!filterEnabled.value){resetElementOpacity(productCard);return}const productCardRating=getFirstElement(PRODUCT_CARD_RATING_SELECTOR,productCard);if(!productCardRating){defineElementOpacity(productCard,!noRatingFilter.value);return}const productCardRatingNumber=getElementInnerNumber(productCardRating);const conditionToDefine=isLessThanFilter(productCardRatingNumber,minRatingFilter);defineElementOpacity(productCard,conditionToDefine)}))}function addDiscountedPriceIfNeeded(productCard){const productCardPrice=getFirstElement(".main-price:not(.__accent)",productCard);if(!productCardPrice)return;if(productCardPrice.classList.contains(DISCOUNTED_PRICE_ADDED_CLASS))return;addDiscountedPrice(productCardPrice)}function addDiscountedPrice(productCardPrice){const productCardPriceNumber=getElementInnerNumber(productCardPrice,true,true);const discountedPrice=productCardPriceNumber*(1-CURRENT_DISCOUNT);const newProductCardPriceText=`${productCardPriceNumber.toFixed()} (${discountedPrice.toFixed()}) ₽`;productCardPrice.innerText=newProductCardPriceText;productCardPrice.classList.add(DISCOUNTED_PRICE_ADDED_CLASS)}})();