// ==UserScript==
// @name         Lenta List Clean
// @description  Remove product cards by filter
// @match        https://*.online.lenta.com/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.3.70185281
// @icon         https://www.google.com/s2/favicons?sz=64&domain=lenta.com
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";function removeNonNumber(stringValue){return stringValue.replace(/[^\d.,-]/g,"")}


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function defineElementOpacity(element,conditionToDefine,opacity=.2){conditionToDefine?setElementOpacity(element,opacity):resetElementOpacity(element)}function resetElementOpacity(element){element.style.opacity=""}function setElementOpacity(element,opacity=.2){element.style.opacity=opacity}function resetElementOrder(element){element.style.order=""}function setElementOrder(element,order){element.style.order=order}function createTextInput(inputOnChange,inputStyle,inputValue){const input=createInput("text",inputOnChange,inputStyle);input.value=inputValue;return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function createCheckboxInput(inputOnChange,inputStyle,isChecked){const input=createInput("checkbox",inputOnChange,inputStyle);input.checked=isChecked;return input}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);if(inputOnChange){input.addEventListener("keyup",inputOnChange);input.addEventListener("change",inputOnChange)}style&&(input.style=style);return input}function createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function getElementInnerNumber(element,cleanText=false,replaceComma=false){const elementText=element.innerText;return parseNumber(elementText,cleanText,replaceComma)}function parseNumber(text,cleanText,replaceComma){cleanText&&(text=removeNonNumber(text));replaceComma&&(text=text.replace(",","."));const number=+text;return number}function getInputValueFromEvent(event){const{target}=event;const{type}=target;switch(type){case"text":return`"${target.value}"`;case"number":return target.value;case"checkbox":return target.checked;default:console.log(`Unknown input type: ${type}`);return null}}class InputValueBase{constructor(value,onChange){this.value=value;this.onChange=onChange}onChangeIfDefined=()=>{this.onChange&&this.onChange()}}function parseValue(value){return value===""?null:JSON.parse(value)}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue){const localStorageItem=storage.getItem(key);return localStorageItem===null?defaultValue:parseValue(localStorageItem)}class StorageValue extends InputValueBase{constructor(storageKey,defaultValue=null,onChange=null){super(getStorageValueOrDefault(storageKey,defaultValue),onChange);this.storageKey=storageKey}updateValueFromEvent=event=>{const newValue=getInputValueFromEvent(event);const newParsedValue=parseValue(newValue);if(this.value===newParsedValue)return;storage.setItem(this.storageKey,newValue);this.value=newParsedValue;this.onChangeIfDefined()}}


function createFilterControlText(titleText,inputValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createTextInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value);filterControl.append(input);return filterControl}function createFilterControlNumber(titleText,inputValue,inputStep,inputMinValue,inputMaxValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createNumberInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createFilterControlCheckbox(titleText,inputValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createCheckboxInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value);filterControl.append(input);return filterControl}function createNameFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlText("Содержит: ",inputValue,controlStyle,inputStyle)}function createMinRatingFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlNumber("Минимальный рейтинг: ",inputValue,"0.1","3.0","5.0",controlStyle,inputStyle)}function createNoRatingFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlCheckbox("Без рейтинга: ",inputValue,controlStyle,inputStyle)}function createEnabledFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlCheckbox("Вкл: ",inputValue,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,force=false,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer){if(!force)return;filtersContainer.remove()}filtersContainer=createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}function isNotMatchTextFilter(parameterValue,filter){return!isMatchTextFilter(parameterValue,filter)}function isMatchTextFilter(parameterValue,filter){if(!filter.value)return true;const comparedString=parameterValue.toLowerCase();const searchStrings=filter.value.toLowerCase().split(",").map((searchString=>searchString.trim()));const{include:includeSearchStrings,notInclude:notIncludeSearchStrings}=searchStrings.reduce(((result,searchString)=>{if(searchString.startsWith("!")){const notIncludeSearchString=searchString.substring(1);notIncludeSearchString.length&&result.notInclude.push(notIncludeSearchString)}else result.include.push(searchString);return result}),{include:[],notInclude:[]});return includeSearchStrings.every((searchString=>comparedString.includes(searchString)))&&notIncludeSearchStrings.every((searchString=>!comparedString.includes(searchString)))}function isLessThanFilter(parameterValue,filter){return filter.value&&parameterValue<filter.value}


const CATEGORY_NAME=getCategoryName();const nameFilter=new StorageValue(`${CATEGORY_NAME}-name-filter`,null);const minRatingFilter=new StorageValue(`${CATEGORY_NAME}-min-rating-filter`,4.1);const noRatingFilter=new StorageValue(`${CATEGORY_NAME}-no-rating-filter`,false);const filterEnabled=new StorageValue(`${CATEGORY_NAME}-filter-enabled`,true);const discountEnabled=new StorageValue("discount-enabled",true);const sortEnabled=new StorageValue("sort-enabled",true);const PRODUCT_CARD_LIST_SELECTOR=".catalog-list";const PRODUCT_CARD_SELECTOR=".catalog-grid_new__item";const PRODUCT_CARD_RATING_SELECTOR=".rating-number";const DISCOUNTED_PRICE_ADDED_CLASS="discountedPriceAdded";const CURRENT_DISCOUNT=.25;function getCategoryName(){const{pathname}=window.location;const pathElements=pathname.split("/");const lastPathElement=pathElements.pop();const categoryName=/^\d+$/.test(lastPathElement)?lastPathElement:"common";return categoryName}setInterval(initListClean,100);function initListClean(){const productCardList=getFirstElement(PRODUCT_CARD_LIST_SELECTOR);if(productCardList){appendFilterControlsIfNeeded(productCardList,appendFiltersContainer);cleanList()}else attachOrderItemsRemoveFunctionIfNeeded()}function attachOrderItemsRemoveFunctionIfNeeded(){const orderPage=getFirstElement("lu-profile-order-page");if(!orderPage)return;const orderItems=getAllElements("lu-profile-order-item",orderPage);orderItems.forEach((orderItem=>{const orderItemWrap=orderItem.parentNode;orderItemWrap.onclick=()=>orderItemWrap.remove()}))}function appendFiltersContainer(filtersContainer,parentNode){filtersContainer.style="display: flex;"+"grid-gap: 15px;"+"margin-left: 10px;";const controlStyle="display: flex;"+"align-items: center;";const inputStyle="margin-left: 5px;"+"border: 1px solid #C9C9C9;"+"border-radius: 8px;"+"height: 40px;"+"padding: 0 16px;";const textInputStyle=inputStyle+// eslint-disable-line prefer-template
"width: 170px;";const numberInputStyle=inputStyle;// eslint-disable-line prefer-template
const checkboxInputStyle="margin-left: 5px;"+"border: 1px solid #C9C9C9;"+"border-radius: 4px;"+"width: 22px;"+"height: 22px;";const nameFilterDiv=createNameFilterControl(nameFilter,controlStyle,textInputStyle);const minRatingDiv=createMinRatingFilterControl(minRatingFilter,controlStyle,numberInputStyle);const noRatingDiv=createNoRatingFilterControl(noRatingFilter,controlStyle,checkboxInputStyle);const filterEnabledDiv=createEnabledFilterControl(filterEnabled,controlStyle,checkboxInputStyle);const discountEnabledDiv=createFilterControlCheckbox("Скидка:",discountEnabled,controlStyle,checkboxInputStyle);const sortEnabledDiv=createFilterControlCheckbox("Сортировка:",sortEnabled,controlStyle,checkboxInputStyle);filtersContainer.append(nameFilterDiv,minRatingDiv,noRatingDiv,filterEnabledDiv,discountEnabledDiv,sortEnabledDiv);parentNode.prepend(filtersContainer)}function cleanList(){const productCards=getAllElements(PRODUCT_CARD_SELECTOR);productCards.forEach((productCard=>{const productCardNameWrap=getFirstElement(".lu-product-card-name_new",productCard);let productCardName="";if(productCardNameWrap){expandProductCardNameWrap(productCardNameWrap);productCardName=productCardNameWrap.innerText}addPriceAttributeIfNeeded(productCard);discountEnabled.value&&addDiscountedPriceIfNeeded(productCard);if(sortEnabled.value){const productCardOrder=productCard.getAttribute("discounted-price")||productCard.getAttribute("price");setElementOrder(productCard,productCardOrder)}else resetElementOrder(productCard);if(!filterEnabled.value){resetElementOpacity(productCard);return}const productCardRating=getFirstElement(PRODUCT_CARD_RATING_SELECTOR,productCard);const minRatingIsNotMatchFilter=productCardRating?isLessThanFilter(getElementInnerNumber(productCardRating),minRatingFilter):!noRatingFilter.value;const conditionToDefine=isNotMatchTextFilter(productCardName,nameFilter)||minRatingIsNotMatchFilter;defineElementOpacity(productCard,conditionToDefine)}))}function expandProductCardNameWrap(productCardNameWrap){productCardNameWrap.style.display="inline-block";productCardNameWrap.style.height="91px"}function addPriceAttributeIfNeeded(productCard){if(productCard.hasAttribute("price"))return;const productCardPrice=getFirstElement(".main-price",productCard);if(!productCardPrice)return;const productCardPriceNumber=getElementInnerNumber(productCardPrice,true,true);productCard.setAttribute("price",productCardPriceNumber.toFixed())}function addDiscountedPriceIfNeeded(productCard){const productCardPrice=getFirstElement(".main-price:not(.__accent)",productCard);if(!productCardPrice)return;if(productCardPrice.classList.contains(DISCOUNTED_PRICE_ADDED_CLASS))return;addDiscountedPrice(productCard,productCardPrice)}function addDiscountedPrice(productCard,productCardPrice){const discountedPriceValue=productCard.getAttribute("discounted-price");const priceValue=productCard.getAttribute("price");let discountedPrice;if(discountedPriceValue)discountedPrice=discountedPriceValue;else{discountedPrice=(priceValue*(1-CURRENT_DISCOUNT)).toFixed();productCard.setAttribute("discounted-price",discountedPrice)}productCardPrice.innerText=`${priceValue} (${discountedPrice}) ₽`;productCardPrice.classList.add(DISCOUNTED_PRICE_ADDED_CLASS)}})();