// ==UserScript==
// @name         Lenta List Clean
// @description  Remove product cards by filter
// @match        https://*.online.lenta.com/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.3.69704250
// @icon         https://www.google.com/s2/favicons?sz=64&domain=lenta.com
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";function removeNonNumber(stringValue){return stringValue.replace(/[^\d.-]/g,"")}


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function hideElement(element){setElementDisplay(element,"none")}function showElement(element,display="block"){setElementDisplay(element,display)}function showHideElement(element,conditionToHide,display="block"){conditionToHide?hideElement(element):showElement(element,display)}function setElementDisplay(element,display){element.style.display=display}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);inputOnChange&&input.addEventListener("change",inputOnChange);style&&(input.style=style);return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function createCheckboxInput(inputOnChange,inputStyle,isChecked){const input=createInput("checkbox",inputOnChange,inputStyle);input.checked=isChecked;return input}function createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function getElementInnerNumber(element,cleanText=false){let elementText=element.innerText;cleanText&&(elementText=removeNonNumber(elementText));const elementNumber=+elementText;return elementNumber}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue){const localStorageItem=storage.getItem(key);return localStorageItem?JSON.parse(localStorageItem):defaultValue}function setStorageValueFromEvent(event,keyName){const{target}=event;const{type}=target;let valueToSet;if(type==="number")valueToSet=target.value;else{if(type!=="checkbox"){console.log(`Unknown input type: ${type}`);return null}valueToSet=target.checked}storage.setItem(keyName,valueToSet);return valueToSet}


function createFilterControlNumber(titleText,inputValue,inputStep,inputMinValue,inputMaxValue,inputOnChange,controlStyle="",inputStyle=""){const filterControl=createDiv(titleText,controlStyle);const input=createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createFilterControlCheckbox(titleText,isChecked,inputOnChange,controlStyle="",inputStyle=""){const filterControl=createDiv(titleText,controlStyle);const input=createCheckboxInput(inputOnChange,inputStyle,isChecked);filterControl.append(input);return filterControl}function createMinRatingFilterControl(inputValue,inputOnChange,controlStyle="",inputStyle=""){return createFilterControlNumber("Минимальный рейтинг: ",inputValue,"0.1","3.0","5.0",inputOnChange,controlStyle,inputStyle)}function createNoRatingFilterControl(isChecked,inputOnChange,controlStyle="",inputStyle=""){return createFilterControlCheckbox("Без рейтинга: ",isChecked,inputOnChange,controlStyle,inputStyle)}function createEnabledFilterControl(isChecked,inputOnChange,controlStyle="",inputStyle=""){return createFilterControlCheckbox("Вкл: ",isChecked,inputOnChange,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer)return;filtersContainer=createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}


const MIN_RATING=4;const NO_RATING=false;const FILTER_ENABLED=true;const MIN_RATING_STORAGE_KEY="min-rating-filter";const NO_RATING_STORAGE_KEY="no-rating-filter";const FILTER_ENABLED_STORAGE_KEY="filter-enabled";const PRODUCT_CARD_LIST_SELECTOR=".catalog-list";const PRODUCT_CARD_SELECTOR=".catalog-grid_new__item";const PRODUCT_CARD_RATING_SELECTOR=".rating-number";let minRatingValue=getStorageValueOrDefault(MIN_RATING_STORAGE_KEY,MIN_RATING);let noRatingChecked=getStorageValueOrDefault(NO_RATING_STORAGE_KEY,NO_RATING);let filterEnabledChecked=getStorageValueOrDefault(FILTER_ENABLED_STORAGE_KEY,FILTER_ENABLED);setInterval(initListClean,500);function initListClean(){const productCardList=getFirstElement(PRODUCT_CARD_LIST_SELECTOR);if(productCardList){appendFilterControlsIfNeeded(productCardList,appendFiltersContainer);cleanList()}}function appendFiltersContainer(filtersContainer,parentNode){filtersContainer.style="display: flex;"+"grid-gap: 15px;"+"margin-left: 10px;";const controlStyle="display: flex;"+"align-items: center;";const numberInputStyle="border: 1px solid #C9C9C9;"+"border-radius: 8px;"+"height: 40px;"+"padding: 0 16px;";const checkboxInputStyle="border: 1px solid #C9C9C9;"+"border-radius: 4px;"+"width: 22px;"+"height: 22px;";const minRatingDiv=createMinRatingFilterControl(minRatingValue,updateMinRatingValue,controlStyle,numberInputStyle);const noRatingDiv=createNoRatingFilterControl(noRatingChecked,updateNoRatingValue,controlStyle,checkboxInputStyle);const filterEnabledDiv=createEnabledFilterControl(filterEnabledChecked,updateFilterEnabledValue,controlStyle,checkboxInputStyle);filtersContainer.append(minRatingDiv,noRatingDiv,filterEnabledDiv);parentNode.prepend(filtersContainer)}function updateMinRatingValue(e){minRatingValue=setStorageValueFromEvent(e,MIN_RATING_STORAGE_KEY)}function updateNoRatingValue(e){noRatingChecked=setStorageValueFromEvent(e,NO_RATING_STORAGE_KEY)}function updateFilterEnabledValue(e){filterEnabledChecked=setStorageValueFromEvent(e,FILTER_ENABLED_STORAGE_KEY)}function cleanList(){const productCards=getAllElements(PRODUCT_CARD_SELECTOR);productCards.forEach((productCard=>{if(!filterEnabledChecked){showElement(productCard);return}const productCardRating=getFirstElement(PRODUCT_CARD_RATING_SELECTOR,productCard);if(!productCardRating){showHideElement(productCard,!noRatingChecked);return}const productCardRatingNumber=getElementInnerNumber(productCardRating);showHideElement(productCard,productCardRatingNumber<minRatingValue)}))}})();