// ==UserScript==
// @name         Megamarket List Clean
// @description  Remove product cards by filter
// @match        https://megamarket.ru/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.1.70965433
// @icon         https://www.google.com/s2/favicons?sz=64&domain=megamarket.ru
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";function removeNonNumber(stringValue){return stringValue.replace(/[^\d.,-]/g,"")}


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function hideElement(element){setElementDisplay(element,"none")}function showElement(element,display="block"){setElementDisplay(element,display)}function showHideElement(element,conditionToHide,display="block"){conditionToHide?hideElement(element):showElement(element,display)}function setElementDisplay(element,display){element.style.display=display}function createTextInput(inputOnChange,inputStyle,inputValue){const input=createInput("text",inputOnChange,inputStyle);input.value=inputValue;return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function createCheckboxInput(inputOnChange,inputStyle,isChecked){const input=createInput("checkbox",inputOnChange,inputStyle);input.checked=isChecked;return input}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);if(inputOnChange){input.addEventListener("keyup",debounce(inputOnChange,200));input.addEventListener("change",debounce(inputOnChange,100))}style&&(input.style=style);return input}function createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function getElementInnerNumber(element,cleanText=false,replaceComma=false){const elementText=element.innerText;return parseNumber(elementText,cleanText,replaceComma)}function parseNumber(text,cleanText,replaceComma){cleanText&&(text=removeNonNumber(text));replaceComma&&(text=text.replace(",","."));const number=+text;return number}function debounce(func,wait=250){let timeoutId;return(...args)=>{clearTimeout(timeoutId);timeoutId=setTimeout((()=>func(...args)),wait)}}function getInputValueFromEvent(event){const{target}=event;const{type}=target;switch(type){case"text":return`"${target.value}"`;case"number":return target.value;case"checkbox":return target.checked;default:console.log(`Unknown input type: ${type}`);return null}}class InputValueBase{constructor(value,onChange){this.value=value;this.onChange=onChange}onChangeIfDefined=()=>{this.onChange&&this.onChange()}}class InputValue extends InputValueBase{constructor(defaultValue=null,onChange=null){super(defaultValue,onChange)}updateValueFromEvent=event=>{const newParsedValue=parseValue(getInputValueFromEvent(event));if(this.value===newParsedValue)return;this.value=newParsedValue;this.onChangeIfDefined()}}function parseValue(value){return value===""?null:JSON.parse(value)}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue=null){const localStorageItem=storage.getItem(key);return localStorageItem===null?defaultValue:parseValue(localStorageItem)}class StoredInputValue extends InputValueBase{constructor(storageKey,defaultValue=null,onChange=null){super(getStorageValueOrDefault(storageKey,defaultValue),onChange);this.storageKey=storageKey}updateValueFromEvent=event=>{const newValue=getInputValueFromEvent(event);const newParsedValue=parseValue(newValue);if(this.value===newParsedValue)return;storage.setItem(this.storageKey,newValue);this.value=newParsedValue;this.onChangeIfDefined()}}


function createFilterControlText(titleText,inputValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createTextInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value);filterControl.append(input);return filterControl}function createFilterControlNumber(titleText,inputValue,inputStep,inputMinValue,inputMaxValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createNumberInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createFilterControlCheckbox(titleText,inputValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createCheckboxInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value);filterControl.append(input);return filterControl}function createNameFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlText("Содержит: ",inputValue,controlStyle,inputStyle)}function createMinCashbackFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlNumber("Минимальный кешбек: ",inputValue,"5","0","100",controlStyle,inputStyle)}function createMaxPriceFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlNumber("Макс. цена: ",inputValue,"500","0","1000000",controlStyle,inputStyle)}function createEnabledFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlCheckbox("Вкл: ",inputValue,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,force=false,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer){if(!force)return;filtersContainer.remove()}filtersContainer=createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}function isNotMatchTextFilter(parameterValue,filter){return!isMatchTextFilter(parameterValue,filter)}function isMatchTextFilter(parameterValue,filter){if(!filter.value)return true;const comparedString=parameterValue.toLowerCase();const searchStrings=filter.value.toLowerCase().split(",").map((searchString=>searchString.trim()));const{include:includeSearchStrings,notInclude:notIncludeSearchStrings}=searchStrings.reduce(((result,searchString)=>{if(searchString.startsWith("!")){const notIncludeSearchString=searchString.substring(1);notIncludeSearchString.length&&result.notInclude.push(notIncludeSearchString)}else result.include.push(searchString);return result}),{include:[],notInclude:[]});return includeSearchStrings.every((searchString=>comparedString.includes(searchString)))&&notIncludeSearchStrings.every((searchString=>!comparedString.includes(searchString)))}function isLessThanFilter(parameterValue,filter){return filter.value&&parameterValue<filter.value}function isGreaterThanFilter(parameterValue,filter){return filter.value&&parameterValue>filter.value}


const nameFilter=new StoredInputValue("name-filter");const minCashbackFilter=new StoredInputValue("min-cashback-filter");const maxPriceFilter=new StoredInputValue("max-price-filter");const filterEnabled=new InputValue(false);const PRODUCT_CARD_LIST_CONTROLS=".catalog-listing-controls";const PRODUCT_CARD_SELECTOR=".catalog-item";const PRODUCT_CARD_PRICE_SELECTOR=".item-price";const PRODUCT_CARD_CASHBACK_SELECTOR=".bonus-percent";const PRICE_ATTR="price";const BALANCED_CASHBACK_PRICE_ATTR="balanced-cashback-price";setInterval(initListClean,100);function initListClean(){const productCardListHeader=getFirstElement(PRODUCT_CARD_LIST_CONTROLS);if(productCardListHeader){appendFilterControlsIfNeeded(productCardListHeader,appendFiltersContainer);cleanList()}}function appendFiltersContainer(filtersContainer,parentNode){filtersContainer.style="display: flex;"+"grid-gap: 15px;"+"padding: 14px 5px;";const controlStyle="display: flex;"+"align-items: center;"+"font-size: 14px;";const inputStyle="border: 1px solid #e4ebf0;"+"font-size: 14px;"+"border-radius: 8px;"+"margin-left: 7px;"+"padding: 8px 14px;";const textInputStyle=inputStyle+// eslint-disable-line prefer-template
"width: 180px;";const numberInputStyle=inputStyle;// eslint-disable-line prefer-template
const checkboxInputStyle="margin-left: 7px;"+"width: 23px;"+"height: 23px;";const nameFilterDiv=createNameFilterControl(nameFilter,controlStyle,textInputStyle);const minCashbackDiv=createMinCashbackFilterControl(minCashbackFilter,controlStyle,numberInputStyle);const maxPriceDiv=createMaxPriceFilterControl(maxPriceFilter,controlStyle,numberInputStyle);const filterEnabledDiv=createEnabledFilterControl(filterEnabled,controlStyle,checkboxInputStyle);filtersContainer.append(nameFilterDiv,minCashbackDiv,maxPriceDiv,filterEnabledDiv);parentNode.append(filtersContainer)}function cleanList(){const productCards=getAllElements(PRODUCT_CARD_SELECTOR);productCards.forEach((productCard=>{if(!filterEnabled.value){showElement(productCard,"flex");return}const productCardNameWrap=getFirstElement(".item-title",productCard);const productCardCashback=getFirstElement(PRODUCT_CARD_CASHBACK_SELECTOR,productCard);if(!productCardNameWrap||!productCardCashback){hideElement(productCard);return}const productCardName=productCardNameWrap.innerText;const productCardCashbackNumber=getElementInnerNumber(productCardCashback,true);const productCardPrice=addBalancedCashbackPriceIfNeeded(productCard,productCardCashbackNumber);
// const price =
//     +productCardPrice.getAttribute(PRICE_ATTR);
const balancedCashbackPrice=+productCardPrice.getAttribute(BALANCED_CASHBACK_PRICE_ATTR);const conditionToHide=isNotMatchTextFilter(productCardName,nameFilter)||isLessThanFilter(productCardCashbackNumber,minCashbackFilter)||isGreaterThanFilter(balancedCashbackPrice,maxPriceFilter);showHideElement(productCard,conditionToHide,"flex")}))}function addBalancedCashbackPriceIfNeeded(productCard,productCardCashbackNumber){const productCardPrice=getFirstElement(PRODUCT_CARD_PRICE_SELECTOR,productCard);productCardPrice.hasAttribute(BALANCED_CASHBACK_PRICE_ATTR)||addBalancedCashbackPrice(productCardPrice,productCardCashbackNumber);return productCardPrice}function addBalancedCashbackPrice(productCardPrice,productCardCashbackNumber){const productCardPriceNumber=getElementInnerNumber(productCardPrice,true);const balancedCashbackPrice=getBalancedCashbackPrice(productCardPriceNumber,productCardCashbackNumber);const productCardPriceSpan=getFirstElement(":scope > span",productCardPrice);const newProductCardPriceSpanText=`${productCardPriceNumber.toLocaleString()} (${balancedCashbackPrice.toLocaleString()}) ₽`;productCardPriceSpan.innerText=newProductCardPriceSpanText;productCardPrice.setAttribute(PRICE_ATTR,productCardPriceNumber);productCardPrice.setAttribute(BALANCED_CASHBACK_PRICE_ATTR,balancedCashbackPrice)}function getBalancedCashbackPrice(price,cashback){const balancedCashbackUsage=getBalancedCashbackUsage(price,cashback);return price-balancedCashbackUsage}function getBalancedCashbackUsage(price,cashback){const cashbackCoeff=cashback/100;return(price*cashbackCoeff/(1+cashbackCoeff)).toFixed(0)}})();