// ==UserScript==
// @name         Megamarket List Clean
// @description  Remove product cards by filter
// @match        https://megamarket.ru/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.1.69757140
// @icon         https://www.google.com/s2/favicons?sz=64&domain=megamarket.ru
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue){const localStorageItem=storage.getItem(key);return localStorageItem?JSON.parse(localStorageItem):defaultValue}function setStorageValueFromEvent(event,keyName){const{target}=event;const{type}=target;let valueToSet;if(type==="number")valueToSet=target.value;else{if(type!=="checkbox"){console.log(`Unknown input type: ${type}`);return null}valueToSet=target.checked}storage.setItem(keyName,valueToSet);return valueToSet}class StorageValue{constructor(storageKey,defaultValue){this.value=getStorageValueOrDefault(storageKey,defaultValue);this.storageKey=storageKey}updateValueFromEvent=event=>{this.value=setStorageValueFromEvent(event,this.storageKey)}}function removeNonNumber(stringValue){return stringValue.replace(/[^\d.-]/g,"")}


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function hideElement(element){setElementDisplay(element,"none")}function showElement(element,display="block"){setElementDisplay(element,display)}function showHideElement(element,conditionToHide,display="block"){conditionToHide?hideElement(element):showElement(element,display)}function setElementDisplay(element,display){element.style.display=display}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);inputOnChange&&input.addEventListener("change",inputOnChange);style&&(input.style=style);return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function createCheckboxInput(inputOnChange,inputStyle,isChecked){const input=createInput("checkbox",inputOnChange,inputStyle);input.checked=isChecked;return input}function createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function getElementInnerNumber(element,cleanText=false){let elementText=element.innerText;cleanText&&(elementText=removeNonNumber(elementText));const elementNumber=+elementText;return elementNumber}


function createFilterControlNumber(titleText,storageValue,inputStep,inputMinValue,inputMaxValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createNumberInput(storageValue.updateValueFromEvent,inputStyle,storageValue.value,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createFilterControlCheckbox(titleText,storageValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createCheckboxInput(storageValue.updateValueFromEvent,inputStyle,storageValue.value);filterControl.append(input);return filterControl}function createMinCashbackFilterControl(storageValue,controlStyle=null,inputStyle=null){return createFilterControlNumber("Минимальный кешбек: ",storageValue,"5","0","100",controlStyle,inputStyle)}function createEnabledFilterControl(storageValue,controlStyle=null,inputStyle=null){return createFilterControlCheckbox("Вкл: ",storageValue,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer)return;filtersContainer=createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}


const minCashbackFilter=new StorageValue("min-cashback-filter",20);const filterEnabled=new StorageValue("filter-enabled",true);const PRODUCT_CARD_LIST_HEADER=".catalog-listing-header";const PRODUCT_CARD_SELECTOR=".catalog-item";const PRODUCT_CARD_CASHBACK_SELECTOR=".bonus-percent";setInterval(initListClean,100);function initListClean(){const productCardListHeader=getFirstElement(PRODUCT_CARD_LIST_HEADER);if(productCardListHeader){appendFilterControlsIfNeeded(productCardListHeader,appendFiltersContainer);cleanList()}}function appendFiltersContainer(filtersContainer,parentNode){filtersContainer.style="display: flex;"+"grid-gap: 15px;"+"padding: 14px 5px;";const controlStyle="display: flex;"+"align-items: center;"+"font-size: 14px;";const inputStyle="border: 1px solid #e4ebf0;"+"font-size: 14px;"+"border-radius: 8px;"+"margin-left: 7px;";const numberInputStyle=inputStyle+// eslint-disable-line prefer-template
"padding: 8px 14px;";const checkboxInputStyle=inputStyle+// eslint-disable-line prefer-template
"width: 23px;"+"height: 23px;";const minCashbackDiv=createMinCashbackFilterControl(minCashbackFilter,controlStyle,numberInputStyle);const filterEnabledDiv=createEnabledFilterControl(filterEnabled,controlStyle,checkboxInputStyle);filtersContainer.append(minCashbackDiv,filterEnabledDiv);parentNode.append(filtersContainer)}function cleanList(){const productCards=getAllElements(PRODUCT_CARD_SELECTOR);productCards.forEach((productCard=>{if(!filterEnabled.value){showElement(productCard,"flex");return}const productCardCashback=getFirstElement(PRODUCT_CARD_CASHBACK_SELECTOR,productCard);if(!productCardCashback){hideElement(productCard);return}const productCardCashbackNumber=getElementInnerNumber(productCardCashback,true);const conditionToHide=productCardCashbackNumber<minCashbackFilter.value;showHideElement(productCard,conditionToHide,"flex")}))}})();