// ==UserScript==
// @name         Pepper List Clean
// @description  Remove product cards by filter
// @match        https://www.pepper.ru/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.3.69704250
// @icon         https://www.google.com/s2/favicons?sz=64&domain=pepper.ru
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";function removeNonNumber(stringValue){return stringValue.replace(/[^\d.-]/g,"")}


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function hideElement(element){setElementDisplay(element,"none")}function showElement(element,display="block"){setElementDisplay(element,display)}function showHideElement(element,conditionToHide,display="block"){conditionToHide?hideElement(element):showElement(element,display)}function setElementDisplay(element,display){element.style.display=display}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);inputOnChange&&input.addEventListener("change",inputOnChange);style&&(input.style=style);return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function createCheckboxInput(inputOnChange,inputStyle,isChecked){const input=createInput("checkbox",inputOnChange,inputStyle);input.checked=isChecked;return input}function createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function getElementInnerNumber(element,cleanText=false){let elementText=element.innerText;cleanText&&(elementText=removeNonNumber(elementText));const elementNumber=+elementText;return elementNumber}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue){const localStorageItem=storage.getItem(key);return localStorageItem?JSON.parse(localStorageItem):defaultValue}function setStorageValueFromEvent(event,keyName){const{target}=event;const{type}=target;let valueToSet;if(type==="number")valueToSet=target.value;else{if(type!=="checkbox"){console.log(`Unknown input type: ${type}`);return null}valueToSet=target.checked}storage.setItem(keyName,valueToSet);return valueToSet}


function createFilterControlNumber(titleText,inputValue,inputStep,inputMinValue,inputMaxValue,inputOnChange,controlStyle="",inputStyle=""){const filterControl=createDiv(titleText,controlStyle);const input=createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createFilterControlCheckbox(titleText,isChecked,inputOnChange,controlStyle="",inputStyle=""){const filterControl=createDiv(titleText,controlStyle);const input=createCheckboxInput(inputOnChange,inputStyle,isChecked);filterControl.append(input);return filterControl}function createMinVotesFilterControl(inputValue,inputOnChange,controlStyle="",inputStyle=""){return createFilterControlNumber("Минимально голосов: ",inputValue,"50","0","10000",inputOnChange,controlStyle,inputStyle)}function createEnabledFilterControl(isChecked,inputOnChange,controlStyle="",inputStyle=""){return createFilterControlCheckbox("Вкл: ",isChecked,inputOnChange,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer)return;filtersContainer=createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}


const MIN_VOTES=100;const SHOW_EXPIRED=false;const FILTER_ENABLED=true;const MIN_VOTES_STORAGE_KEY="min-votes-filter";const SHOW_EXPIRED_STORAGE_KEY="show-expired-filter";const FILTER_ENABLED_STORAGE_KEY="filter-enabled";const PRODUCT_CARD_LIST_SELECTOR=".listLayout-main";const PRODUCT_CARD_SELECTOR=".thread--type-list:not(.js-telegram-widget)";const PRODUCT_CARD_RATING_SELECTOR=".vote-box > span";let minVotesValue=getStorageValueOrDefault(MIN_VOTES_STORAGE_KEY,MIN_VOTES);let showExpiredChecked=getStorageValueOrDefault(SHOW_EXPIRED_STORAGE_KEY,SHOW_EXPIRED);let filterEnabledChecked=getStorageValueOrDefault(FILTER_ENABLED_STORAGE_KEY,FILTER_ENABLED);setInterval(initListClean,100);const productCardList=getFirstElement(PRODUCT_CARD_LIST_SELECTOR);function initListClean(){const productCards=getAllElements(PRODUCT_CARD_SELECTOR);if(productCardList&&productCards.length){appendFilterControlsIfNeeded(productCardList,appendFiltersContainer);cleanList(productCards)}}function appendFiltersContainer(filtersContainer,parentNode){filtersContainer.style="display: flex;"+"grid-gap: 15px;"+"margin-top: 0.5em;"+"padding: 11px 19px;"+"background-color: #fff;"+"border-radius: 8px;";const controlStyle="display: flex;"+"align-items: center;";const inputStyle="border: 1px solid #d1d5db;"+"border-radius: 8px;"+"margin-left: 7px;";const numberInputStyle=inputStyle+// eslint-disable-line prefer-template
"padding: 7px 14px;"+"background-color: #fff;";const checkboxInputStyle=inputStyle+// eslint-disable-line prefer-template
"width: 24px;"+"height: 24px;";const minVotesDiv=createMinVotesFilterControl(minVotesValue,updateMinVotesValue,controlStyle,numberInputStyle);const showExpiredDiv=createFilterControlCheckbox("Завершённые: ",showExpiredChecked,updateShowExpiredValue,controlStyle,checkboxInputStyle);const filterEnabledDiv=createEnabledFilterControl(filterEnabledChecked,updateFilterEnabledValue,controlStyle,checkboxInputStyle);filtersContainer.append(minVotesDiv,showExpiredDiv,filterEnabledDiv);parentNode.prepend(filtersContainer)}function updateMinVotesValue(e){minVotesValue=setStorageValueFromEvent(e,MIN_VOTES_STORAGE_KEY)}function updateShowExpiredValue(e){showExpiredChecked=setStorageValueFromEvent(e,SHOW_EXPIRED_STORAGE_KEY)}function updateFilterEnabledValue(e){filterEnabledChecked=setStorageValueFromEvent(e,FILTER_ENABLED_STORAGE_KEY)}function cleanList(productCards){productCards.forEach((productCard=>{if(!filterEnabledChecked){showElement(productCard);return}const isExpired=productCard.classList.contains("thread--expired");if(isExpired&&!showExpiredChecked){hideElement(productCard);return}const productCardRating=getFirstElement(PRODUCT_CARD_RATING_SELECTOR,productCard);const productCardRatingNumber=getElementInnerNumber(productCardRating,true);showHideElement(productCard,productCardRatingNumber<minVotesValue)}))}})();