// ==UserScript==
// @name         Pepper List Clean
// @description  Remove product cards by filter
// @match        https://www.pepper.ru/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.3.69997775
// @icon         https://www.google.com/s2/favicons?sz=64&domain=pepper.ru
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";function removeNonNumber(stringValue){return stringValue.replace(/[^\d.,-]/g,"")}


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function defineElementOpacity(element,conditionToDefine,opacity=.2){conditionToDefine?setElementOpacity(element,opacity):resetElementOpacity(element)}function resetElementOpacity(element){element.style.opacity=1}function setElementOpacity(element,opacity=.2){element.style.opacity=opacity}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);inputOnChange&&input.addEventListener("change",inputOnChange);style&&(input.style=style);return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function createCheckboxInput(inputOnChange,inputStyle,isChecked){const input=createInput("checkbox",inputOnChange,inputStyle);input.checked=isChecked;return input}function createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function getElementInnerNumber(element,cleanText=false,replaceComma=false){let elementText=element.innerText;cleanText&&(elementText=removeNonNumber(elementText));replaceComma&&(elementText=elementText.replace(",","."));const elementNumber=+elementText;return elementNumber}function waitForElement(parentNode,selector,timeout=null){return new Promise((resolve=>{const observer=new MutationObserver(mutationCallback);observer.observe(parentNode,{childList:true,subtree:true});let timeoutId=null;timeout&&(timeoutId=setTimeout((()=>{observer.disconnect();console.log(`No element found for selector: ${selector}`);resolve(null)}),timeout));function mutationCallback(){if(parentNode.querySelector(selector)){timeoutId&&clearTimeout(timeoutId);observer.disconnect();resolve(parentNode.querySelector(selector))}}}))}function getInputValueFromEvent(event){const{target}=event;const{type}=target;let inputValue;if(type==="number")inputValue=target.value;else{if(type!=="checkbox"){console.log(`Unknown input type: ${type}`);return null}inputValue=target.checked}return parseValue(inputValue)}class InputValueBase{constructor(value,onChange){this.value=value;this.onChange=onChange}onChangeIfDefined=()=>{this.onChange&&this.onChange()}}function parseValue(value){return value===""?null:JSON.parse(value)}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue){const localStorageItem=storage.getItem(key);return localStorageItem===null?defaultValue:parseValue(localStorageItem)}function setStorageValueFromEvent(event,keyName){const inputValue=getInputValueFromEvent(event);storage.setItem(keyName,inputValue);return inputValue}class StorageValue extends InputValueBase{constructor(storageKey,defaultValue=null,onChange=null){super(getStorageValueOrDefault(storageKey,defaultValue),onChange);this.storageKey=storageKey}updateValueFromEvent=event=>{this.value=setStorageValueFromEvent(event,this.storageKey);this.onChangeIfDefined()}}


function createFilterControlNumber(titleText,inputValue,inputStep,inputMinValue,inputMaxValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createNumberInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createFilterControlCheckbox(titleText,inputValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createCheckboxInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value);filterControl.append(input);return filterControl}function createMinVotesFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlNumber("Минимально голосов: ",inputValue,"50","0","10000",controlStyle,inputStyle)}function createEnabledFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlCheckbox("Вкл: ",inputValue,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer)return;filtersContainer=createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}function isLessThanFilter(parameterValue,filter){return filter.value&&parameterValue<filter.value}


const minVotesFilter=new StorageValue("min-votes-filter",100);const showExpiredFilter=new StorageValue("show-expired-filter",false);const filterEnabled=new StorageValue("filter-enabled",true);const PRODUCT_CARD_LIST_SELECTOR=".listLayout-main";const PRODUCT_CARD_SELECTOR=".thread--type-list:not(.js-telegram-widget)";const PRODUCT_CARD_RATING_SELECTOR=".vote-box > span";const DISCUSSIONS_SELECTOR=":scope > div:not(.js-vue2):not(.vue-portal-target)";setInterval(initListClean,100);makeDiscussionsSticky();const productCardList=getFirstElement(PRODUCT_CARD_LIST_SELECTOR);function initListClean(){const productCards=getAllElements(PRODUCT_CARD_SELECTOR);if(productCardList&&productCards.length){appendFilterControlsIfNeeded(productCardList,appendFiltersContainer);cleanList(productCards)}}function appendFiltersContainer(filtersContainer,parentNode){const observerCallback=([e])=>{e.target.style.boxShadow=e.intersectionRatio<1?"0px 0px 10px":"none"};const observer=new IntersectionObserver(observerCallback,{threshold:[1]});observer.observe(filtersContainer);filtersContainer.style="display: flex;"+"grid-gap: 15px;"+"margin-top: 0.5em;"+"padding: 11px 19px;"+"background-color: #fff;"+"border-radius: 8px;"+"position: sticky;"+"z-index: 50;"+"top: -1px;";const controlStyle="display: flex;"+"align-items: center;";const inputStyle="border: 1px solid #d1d5db;"+"border-radius: 8px;"+"margin-left: 7px;";const numberInputStyle=inputStyle+// eslint-disable-line prefer-template
"padding: 7px 14px;"+"background-color: #fff;";const checkboxInputStyle=inputStyle+// eslint-disable-line prefer-template
"width: 24px;"+"height: 24px;";const minVotesDiv=createMinVotesFilterControl(minVotesFilter,controlStyle,numberInputStyle);const showExpiredDiv=createFilterControlCheckbox("Завершённые: ",showExpiredFilter,controlStyle,checkboxInputStyle);const filterEnabledDiv=createEnabledFilterControl(filterEnabled,controlStyle,checkboxInputStyle);filtersContainer.append(minVotesDiv,showExpiredDiv,filterEnabledDiv);parentNode.prepend(filtersContainer)}function cleanList(productCards){productCards.forEach((productCard=>{if(!filterEnabled.value){resetElementOpacity(productCard);return}const isExpired=productCard.classList.contains("thread--expired");if(isExpired&&!showExpiredFilter.value){setElementOpacity(productCard);return}const productCardRating=getFirstElement(PRODUCT_CARD_RATING_SELECTOR,productCard);const productCardRatingNumber=getElementInnerNumber(productCardRating,true);const conditionToDefine=isLessThanFilter(productCardRatingNumber,minVotesFilter);defineElementOpacity(productCard,conditionToDefine)}))}function makeDiscussionsSticky(){const listLayoutSide=getFirstElement(".listLayout-side");waitForElement(listLayoutSide,DISCUSSIONS_SELECTOR,2e3).then((discussions=>{if(!discussions)return;discussions.style.position="sticky";discussions.style.top="0"}))}})();