// ==UserScript==
// @name         Prodoctorov List Clean
// @description  Remove profile cards by filter
// @match        https://prodoctorov.ru/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.6.69688036
// @icon         https://www.google.com/s2/favicons?sz=64&domain=prodoctorov.ru
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";function removeNonNumber(stringValue){return stringValue.replace(/[^\d.-]/g,"")}


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function hideElement(element){setElementDisplay(element,"none")}function showElement(element,display="block"){setElementDisplay(element,display)}function setElementDisplay(element,display){element.style.display=display}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);inputOnChange&&input.addEventListener("change",inputOnChange);style&&(input.style=style);return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function dom_createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function createLink(href,innerHTML=null,style=null){const link=document.createElement("a");link.href=href;innerHTML&&(link.innerHTML=innerHTML);style&&(link.style=style);return link}function getElementInnerNumber(element,cleanText=false){let elementText=element.innerText;cleanText&&(elementText=removeNonNumber(elementText));const elementNumber=+elementText;return elementNumber}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue){const localStorageItem=storage.getItem(key);return localStorageItem?JSON.parse(localStorageItem):defaultValue}function setStorageValueFromEvent(event,keyName){const{target}=event;const{type}=target;let valueToSet;if(type==="number")valueToSet=target.value;else{if(type!=="checkbox"){console.log(`Unknown input type: ${type}`);return null}valueToSet=target.checked}storage.setItem(keyName,valueToSet);return valueToSet}


function createFilterControlNumber(titleText,inputValue,inputStep,inputMinValue,inputMaxValue,inputOnChange,controlStyle="",inputStyle=""){const filterControl=dom_createDiv(titleText,controlStyle);const input=createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createMinReviewsFilterControl(inputValue,inputOnChange,controlStyle="",inputStyle=""){return createFilterControlNumber("Минимально отзывов: ",inputValue,"1","1","999999",inputOnChange,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer)return;filtersContainer=dom_createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}


const MIN_REVIEWS_LOCAL_STORAGE_KEY="minReviewsFilter";const APPOINTMENTS_PAGE=".appointments_page";const SPECIAL_PLACEMENT_CARD_SELECTOR=".b-doctor-card_special-placement";const DOCTOR_CARD_SELECTOR=".b-doctor-card";const DOCTOR_CARD_NAME_SELECTOR=".b-doctor-card__name-surname";const ADDITIONAL_LINKS_APPENDED_CLASS="additionalLinksAppended";const MIN_REVIEWS=10;let minReviewsValue=getStorageValueOrDefault(MIN_REVIEWS_LOCAL_STORAGE_KEY,MIN_REVIEWS);const appointmentsPage=getFirstElement(APPOINTMENTS_PAGE);appointmentsPage?setInterval(initListClean,100):appendReviewsInfoToHeader();function initListClean(){removeSpecialPlacementCards();appendFilterControlsIfNeeded(appointmentsPage,appendFiltersContainer);cleanList()}function appendFiltersContainer(filtersContainer,parentNode){const minReviewsDiv=createMinReviewsFilterControl(minReviewsValue,updateMinReviewsValue);filtersContainer.append(minReviewsDiv);parentNode.prepend(filtersContainer)}function updateMinReviewsValue(e){minReviewsValue=setStorageValueFromEvent(e,MIN_REVIEWS_LOCAL_STORAGE_KEY)}function removeSpecialPlacementCards(){const specialPlacementCards=getAllElements(SPECIAL_PLACEMENT_CARD_SELECTOR);specialPlacementCards.forEach((specialPlacementCard=>specialPlacementCard.remove()))}function cleanList(){const doctorCards=getAllElements(DOCTOR_CARD_SELECTOR,appointmentsPage);doctorCards.forEach((doctorCard=>{const profileCard=getFirstElement(".b-profile-card",doctorCard,true);const reviewsLink=getFirstElement(":scope > a",profileCard);if(!reviewsLink){doctorCard.remove();return}const reviewsLinkNumber=getElementInnerNumber(reviewsLink,true);reviewsLinkNumber<minReviewsValue?hideElement(doctorCard):showElement(doctorCard,"flex");appendAdditionalLinks(doctorCard,profileCard)}))}function appendAdditionalLinks(doctorCard,profileCard){if(profileCard.classList.contains(ADDITIONAL_LINKS_APPENDED_CLASS))return;appendAdditionalLink(doctorCard,profileCard,"НаПоправку");appendAdditionalLink(doctorCard,profileCard,"DocDoc");appendAdditionalLink(doctorCard,profileCard,"Докту");profileCard.classList.add(ADDITIONAL_LINKS_APPENDED_CLASS)}function appendAdditionalLink(doctorCard,profileCard,siteName){const doctorCardName=getFirstElement(DOCTOR_CARD_NAME_SELECTOR,doctorCard,true);const doctorName=doctorCardName.innerText;const searchString=`${doctorName} ${siteName}`;const encodedSearchString=encodeURIComponent(searchString);const lineBreak=document.createElement("br");const searchUrlLink=createLink(`https://www.google.com/search?q=${encodedSearchString}&btnI`,siteName);profileCard.append(lineBreak,searchUrlLink)}function appendReviewsInfoToHeader(){const reviewsFilter=getFirstElement(".reviews-filter:not(.b-reviews-page__filter)");if(!reviewsFilter)return;const nameSpanHolder=getFirstElement(".b-doctor-intro__title-first-line",document,true);if(!nameSpanHolder)return;const nameSpan=getFirstElement('[itemprop="name"]',nameSpanHolder,true);if(!nameSpan)return;const reviewsInfo=dom_createDiv();reviewsInfo.classList.add("v-application");const reviewsFilterSpans=getAllElements(":scope > span",reviewsFilter,true);const lastReviewsFilterSpansIndex=reviewsFilterSpans.length-1;for(let i=1;i<=lastReviewsFilterSpansIndex;i+=1){const reviewsFilterSpan=reviewsFilterSpans[i];const reviewsFilterSpanCopy=reviewsFilterSpan.cloneNode(true);reviewsFilterSpanCopy.addEventListener("click",scrollToParentAndClick(reviewsFilterSpan));reviewsInfo.append(reviewsFilterSpanCopy)}nameSpan.append(reviewsInfo)}function scrollToParentAndClick(element){return()=>{element.parentNode.scrollIntoView({behavior:"smooth"});element.click()}}})();