// ==UserScript==
// @name         WB List Clean
// @description  Remove product cards by filter
// @match        https://www.wildberries.ru/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.7.69704250
// @icon         https://www.google.com/s2/favicons?sz=64&domain=wildberries.ru
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";function removeNonNumber(stringValue){return stringValue.replace(/[^\d.-]/g,"")}


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function hideElement(element){setElementDisplay(element,"none")}function showElement(element,display="block"){setElementDisplay(element,display)}function showHideElement(element,conditionToHide,display="block"){conditionToHide?hideElement(element):showElement(element,display)}function setElementDisplay(element,display){element.style.display=display}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);inputOnChange&&input.addEventListener("change",inputOnChange);style&&(input.style=style);return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function createCheckboxInput(inputOnChange,inputStyle,isChecked){const input=createInput("checkbox",inputOnChange,inputStyle);input.checked=isChecked;return input}function createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function getFirstElementInnerNumber(parentNode,selector,cleanText){const element=getFirstElement(selector,parentNode,true);const elementNumber=getElementInnerNumber(element,cleanText);return elementNumber}function getElementInnerNumber(element,cleanText=false){let elementText=element.innerText;cleanText&&(elementText=removeNonNumber(elementText));const elementNumber=+elementText;return elementNumber}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue){const localStorageItem=storage.getItem(key);return localStorageItem?JSON.parse(localStorageItem):defaultValue}function setStorageValueFromEvent(event,keyName){const{target}=event;const{type}=target;let valueToSet;if(type==="number")valueToSet=target.value;else{if(type!=="checkbox"){console.log(`Unknown input type: ${type}`);return null}valueToSet=target.checked}storage.setItem(keyName,valueToSet);return valueToSet}


function createFilterControlNumber(titleText,inputValue,inputStep,inputMinValue,inputMaxValue,inputOnChange,controlStyle="",inputStyle=""){const filterControl=createDiv(titleText,controlStyle);const input=createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createFilterControlCheckbox(titleText,isChecked,inputOnChange,controlStyle="",inputStyle=""){const filterControl=createDiv(titleText,controlStyle);const input=createCheckboxInput(inputOnChange,inputStyle,isChecked);filterControl.append(input);return filterControl}function createMinRatingFilterControl(inputValue,inputOnChange,controlStyle="",inputStyle=""){return createFilterControlNumber("Минимальный рейтинг: ",inputValue,"0.1","3.0","5.0",inputOnChange,controlStyle,inputStyle)}function createMinReviewsFilterControl(inputValue,inputOnChange,controlStyle="",inputStyle=""){return createFilterControlNumber("Минимально отзывов: ",inputValue,"1","1","999999",inputOnChange,controlStyle,inputStyle)}function createEnabledFilterControl(isChecked,inputOnChange,controlStyle="",inputStyle=""){return createFilterControlCheckbox("Вкл: ",isChecked,inputOnChange,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer)return;filtersContainer=createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}


const MIN_REVIEWS=50;const MIN_RATING=4.8;const FILTER_ENABLED=true;const CATEGORY_NAME=getCategoryName();const MIN_REVIEWS_STORAGE_KEY=`${CATEGORY_NAME}-min-reviews-filter`;const MIN_RATING_STORAGE_KEY=`${CATEGORY_NAME}-min-rating-filter`;const FILTER_ENABLED_STORAGE_KEY=`${CATEGORY_NAME}-filter-enabled`;const FILTERS_BLOCK_WRAP_SELECTOR=".filters-block__wrap";const PRODUCT_CARD_SELECTOR=".product-card";const PRODUCT_CARD_REVIEWS_SELECTOR=".product-card__count";const PRODUCT_CARD_RATING_SELECTOR=".address-rate-mini";const PRODUCT_CARD_PRICE_SELECTOR=".price__lower-price";const PRICE_FILTER_URL_PARAMS_NAME="priceU";let minReviewsValue=getStorageValueOrDefault(MIN_REVIEWS_STORAGE_KEY,MIN_REVIEWS);let minRatingValue=getStorageValueOrDefault(MIN_RATING_STORAGE_KEY,MIN_RATING);let minPriceValue=getMinPriceValueFromURL();let filterEnabledChecked=getStorageValueOrDefault(FILTER_ENABLED_STORAGE_KEY,FILTER_ENABLED);const minPriceDivTextContent=()=>`Минимальная цена: ${minPriceValue}`;function getCategoryName(){const{pathname}=window.location;const pathElements=pathname.split("/");const lastPathElement=pathElements.pop();const categoryName=lastPathElement||"common";return categoryName}function getMinPriceValueFromURL(){const params=new URLSearchParams(window.location.search);if(!params.has(PRICE_FILTER_URL_PARAMS_NAME))return 0;const priceFilterParams=params.get(PRICE_FILTER_URL_PARAMS_NAME);const priceFilterParamsArray=priceFilterParams.split(";");const minPriceFilterParam=priceFilterParamsArray[0];const minPriceFilterValue=minPriceFilterParam/100;return minPriceFilterValue}setInterval(initListClean,100);function initListClean(){const filtersBlockWrap=getFirstElement(FILTERS_BLOCK_WRAP_SELECTOR);if(filtersBlockWrap){removeRecentItemsBlock();appendFilterControlsIfNeeded(filtersBlockWrap,appendFiltersContainer);cleanList()}}function removeRecentItemsBlock(){const recentItems=getFirstElement(".j-recent-items");if(recentItems){const{parentNode}=recentItems;parentNode.remove()}}function appendFiltersContainer(filtersContainer,parentNode){filtersContainer.style="display: flex;"+"grid-gap: 15px;"+"margin-top: 14px;";const controlStyle="display: flex;"+"align-items: center;";const priceControlStyle=controlStyle+// eslint-disable-line prefer-template
"margin-right: 37px;";const inputStyle="margin: 0px 4px;";const numberInputStyle=inputStyle+// eslint-disable-line prefer-template
"width: 55px;";const checkboxInputStyle=inputStyle+// eslint-disable-line prefer-template
"width: 22px;"+"height: 22px;";const minReviewsDiv=createMinReviewsFilterControl(minReviewsValue,updateMinReviewsValue,controlStyle,numberInputStyle);const minRatingDiv=createMinRatingFilterControl(minRatingValue,updateMinRatingValue,controlStyle,numberInputStyle);const minPriceDiv=createDiv(minPriceDivTextContent(),priceControlStyle);const filterEnabledDiv=createEnabledFilterControl(filterEnabledChecked,updateFilterEnabledValue,controlStyle,checkboxInputStyle);setInterval((()=>checkMinPrice(minPriceDiv)),500);filtersContainer.append(minReviewsDiv,minRatingDiv,minPriceDiv,filterEnabledDiv);parentNode.append(filtersContainer)}function updateMinReviewsValue(e){minReviewsValue=setStorageValueFromEvent(e,MIN_REVIEWS_STORAGE_KEY)}function updateMinRatingValue(e){minRatingValue=setStorageValueFromEvent(e,MIN_RATING_STORAGE_KEY)}function updateFilterEnabledValue(e){filterEnabledChecked=setStorageValueFromEvent(e,FILTER_ENABLED_STORAGE_KEY)}function checkMinPrice(minPriceDiv){const currentMinPriceValue=getMinPriceValueFromURL();if(minPriceValue!==currentMinPriceValue){minPriceValue=currentMinPriceValue;minPriceDiv.textContent=minPriceDivTextContent()}}function cleanList(){const productCards=getAllElements(PRODUCT_CARD_SELECTOR);productCards.forEach((productCard=>{if(!filterEnabledChecked){showElement(productCard);return}const productCardReviewsNumber=getFirstElementInnerNumber(productCard,PRODUCT_CARD_REVIEWS_SELECTOR,true);const productCardRatingNumber=getFirstElementInnerNumber(productCard,PRODUCT_CARD_RATING_SELECTOR);const productCardPriceNumber=getFirstElementInnerNumber(productCard,PRODUCT_CARD_PRICE_SELECTOR,true);const conditionToHide=productCardReviewsNumber<minReviewsValue||productCardRatingNumber<minRatingValue||productCardPriceNumber<minPriceValue;showHideElement(productCard,conditionToHide)}))}})();