// ==UserScript==
// @name         Yamarket List Clean
// @description  Remove product cards by filter
// @match        https://market.yandex.ru/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.4.69688036
// @icon         https://www.google.com/s2/favicons?sz=64&domain=market.yandex.ru
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";function removeNonNumber(stringValue){return stringValue.replace(/[^\d.-]/g,"")}


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function insertAfter(existingNode,newNode){existingNode.parentNode.insertBefore(newNode,existingNode.nextSibling)}function hideElement(element){setElementDisplay(element,"none")}function showElement(element,display="block"){setElementDisplay(element,display)}function setElementDisplay(element,display){element.style.display=display}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);inputOnChange&&input.addEventListener("change",inputOnChange);style&&(input.style=style);return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function dom_createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function getElementInnerNumber(element,cleanText=false){let elementText=element.innerText;cleanText&&(elementText=removeNonNumber(elementText));const elementNumber=+elementText;return elementNumber}function getArrayElementInnerNumber(array,elementIndex,cleanText=false){const element=array[elementIndex];const elementNumber=getElementInnerNumber(element,cleanText);return elementNumber}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue){const localStorageItem=storage.getItem(key);return localStorageItem?JSON.parse(localStorageItem):defaultValue}function setStorageValueFromEvent(event,keyName){const{target}=event;const{type}=target;let valueToSet;if(type==="number")valueToSet=target.value;else{if(type!=="checkbox"){console.log(`Unknown input type: ${type}`);return null}valueToSet=target.checked}storage.setItem(keyName,valueToSet);return valueToSet}


function createFilterControlNumber(titleText,inputValue,inputStep,inputMinValue,inputMaxValue,inputOnChange,controlStyle="",inputStyle=""){const filterControl=dom_createDiv(titleText,controlStyle);const input=createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createMinRatingFilterControl(inputValue,inputOnChange,controlStyle="",inputStyle=""){return createFilterControlNumber("Минимальный рейтинг: ",inputValue,"0.1","3.0","5.0",inputOnChange,controlStyle,inputStyle)}function createMinReviewsFilterControl(inputValue,inputOnChange,controlStyle="",inputStyle=""){return createFilterControlNumber("Минимально отзывов: ",inputValue,"1","1","999999",inputOnChange,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer)return;filtersContainer=dom_createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}


const MIN_REVIEWS=5;const MIN_RATING=4.8;const SEARCH_CONTROLS_SELECTOR='[data-apiary-widget-name="@marketplace/SearchControls"]';const VIRTUOSO_SCROLLER_SELECTOR='[data-virtuoso-scroller="true"]';const PRODUCT_CARD_SNIPPET_SELECTOR='[data-autotest-id="product-snippet"]';const PRODUCT_CARD_PARENT_ATTRIBUTE="data-apiary-widget-name";const CATEGORY_NAME=getCategoryName();const MIN_REVIEWS_LOCAL_STORAGE_KEY=`${CATEGORY_NAME}-min-reviews-filter`;const MIN_RATING_LOCAL_STORAGE_KEY=`${CATEGORY_NAME}-min-rating-filter`;let minReviewsValue=getStorageValueOrDefault(MIN_REVIEWS_LOCAL_STORAGE_KEY,MIN_REVIEWS);let minRatingValue=getStorageValueOrDefault(MIN_RATING_LOCAL_STORAGE_KEY,MIN_RATING);function getCategoryName(){const{pathname}=window.location;const pathElements=pathname.split("/");const categoryName=pathElements[1]??"common";return categoryName}const searchControls=getFirstElement(SEARCH_CONTROLS_SELECTOR);if(searchControls){appendFilterControlsIfNeeded(searchControls,appendFiltersContainer);setInterval(cleanList,500)}function appendFiltersContainer(filterControls,parentNode){filterControls.style="display: flex;"+"gap: 10px;"+"padding: 0 10px 15px;"+"font-size: 16px;"+"font-weight: 500;";const inputStyle="border-radius: 7px;"+"border: none;"+"padding: 9px 11px;"+"box-shadow: inset 0 0 0 1.5px #d2d0cc;";const minReviewsDiv=createMinReviewsFilterControl(minReviewsValue,updateMinReviewsValue,"",inputStyle);const minRatingDiv=createMinRatingFilterControl(minRatingValue,updateMinRatingValue,"",inputStyle);filterControls.append(minReviewsDiv,minRatingDiv);insertAfter(parentNode,filterControls)}function updateMinReviewsValue(e){minReviewsValue=setStorageValueFromEvent(e,MIN_REVIEWS_LOCAL_STORAGE_KEY)}function updateMinRatingValue(e){minRatingValue=setStorageValueFromEvent(e,MIN_RATING_LOCAL_STORAGE_KEY)}function cleanList(){const virtuosoScrollers=getAllElements(VIRTUOSO_SCROLLER_SELECTOR);virtuosoScrollers.forEach((virtuosoScroller=>{virtuosoScroller.style.minHeight="0"}));const productCardSnippets=getAllElements(PRODUCT_CARD_SNIPPET_SELECTOR);productCardSnippets.forEach((productCardSnippet=>{const productCardSnippetParent=productCardSnippet.parentNode;const isFirstLoad=productCardSnippetParent.hasAttribute(PRODUCT_CARD_PARENT_ATTRIBUTE);const productCard=isFirstLoad?productCardSnippetParent.parentNode.parentNode:productCardSnippetParent;const ratingMeter=getFirstElement('[role="meter"]',productCardSnippet);if(!ratingMeter){productCard.remove();return}const ratingInfoWrap=ratingMeter.parentNode;const ratingInfoSpans=getAllElements(":scope > span",ratingInfoWrap);const productCardReviewsNumber=getArrayElementInnerNumber(ratingInfoSpans,1);const productCardRatingNumber=getArrayElementInnerNumber(ratingInfoSpans,0);productCardReviewsNumber<minReviewsValue||productCardRatingNumber<minRatingValue?hideElement(productCard):showElement(productCard)}))}})();