// ==UserScript==
// @name         Yamarket List Clean
// @description  Remove product cards by filter
// @match        https://market.yandex.ru/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.5.73187003
// @icon         https://www.google.com/s2/favicons?sz=64&domain=market.yandex.ru
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";function debounce(func,wait=250){let timeoutId;return(...args)=>{clearTimeout(timeoutId);timeoutId=setTimeout((()=>func(...args)),wait)}}


class InputValueBase{constructor(value,onChange){this.value=value;this.onChange=onChange}onChangeIfDefined=()=>{this.onChange&&this.onChange()}}


function getInputValueFromEvent(event){const{target}=event;const{type}=target;switch(type){case"text":return`"${target.value}"`;case"number":return target.value;case"checkbox":return target.checked;default:console.log(`Unknown input type: ${type}`);return null}}


function parseValue(value){return value===""?null:JSON.parse(value)}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue=null){const localStorageItem=storage.getItem(key);return localStorageItem===null?defaultValue:parseValue(localStorageItem)}class StoredInputValue extends InputValueBase{constructor(storageKey,defaultValue=null,onChange=null){super(getStorageValueOrDefault(storageKey,defaultValue),onChange);this.storageKey=storageKey}updateValueFromEvent=event=>{const newValue=getInputValueFromEvent(event);const newParsedValue=parseValue(newValue);if(this.value===newParsedValue)return;storage.setItem(this.storageKey,newValue);this.value=newParsedValue;this.onChangeIfDefined()}}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function createCheckboxInput(inputOnChange,inputStyle,isChecked){const input=createInput("checkbox",inputOnChange,inputStyle);input.checked=isChecked;return input}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);if(inputOnChange){input.addEventListener("keyup",debounce(inputOnChange,200));input.addEventListener("change",debounce(inputOnChange,100))}style&&(input.style=style);return input}function elementsFactory_createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function removeNonNumber(stringValue){return stringValue.replace(/[^\d.,-]/g,"")}


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function getElementInnerNumber(element,cleanText=false,replaceComma=false,defaultValue=null){if(!element){if(defaultValue!==null)return defaultValue;console.log("No element found")}const elementText=element.innerText;return parseNumber(elementText,cleanText,replaceComma)}function parseNumber(text,cleanText,replaceComma){cleanText&&(text=removeNonNumber(text));replaceComma&&(text=text.replace(",","."));const number=+text;return number}


function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,force=false,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer){if(!force)return;filtersContainer.remove()}filtersContainer=elementsFactory_createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}function isLessThanFilter(parameterValue,filter){return filter.value&&parameterValue<filter.value}function createFilterControlNumber(titleText,inputValue,inputStep,inputMinValue,inputMaxValue,controlStyle=null,inputStyle=null){const filterControl=elementsFactory_createDiv(titleText,controlStyle);const input=createNumberInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createFilterControlCheckbox(titleText,inputValue,controlStyle=null,inputStyle=null){const filterControl=elementsFactory_createDiv(titleText,controlStyle);const input=createCheckboxInput(inputValue.updateValueFromEvent,inputStyle,inputValue.value);filterControl.append(input);return filterControl}function createMinRatingFilterControl(inputValue,controlStyle=null,inputStyle=null,inputStep=.1){return createFilterControlNumber("Мин. рейтинг: ",inputValue,inputStep,3,5,controlStyle,inputStyle)}function createMinReviewsFilterControl(inputValue,controlStyle=null,inputStyle=null){return createReviewsFilterControl("Мин. отзывов: ",inputValue,controlStyle,inputStyle)}function createReviewsFilterControl(titleText,inputValue,controlStyle=null,inputStyle=null){return createFilterControlNumber(titleText,inputValue,"1","1","999999",controlStyle,inputStyle)}function createEnabledFilterControl(inputValue,controlStyle=null,inputStyle=null){return createFilterControlCheckbox("Вкл: ",inputValue,controlStyle,inputStyle)}


function insertAfter(existingNode,newNode){existingNode.parentNode.insertBefore(newNode,existingNode.nextSibling)}function hideElement(element){setElementDisplayAttributeIfNeeded(element);setElementDisplay(element,"none")}function showElement(element,display){display=display||getElementDisplay(element);setElementDisplay(element,display)}function showHideElement(element,conditionToHide,display){if(conditionToHide)hideElement(element);else{display=display||getElementDisplay(element);showElement(element,display)}}function setElementDisplay(element,display){element.style.display=display}function getElementDisplay(element){setElementDisplayAttributeIfNeeded(element);return element.getAttribute("display")}function setElementDisplayAttributeIfNeeded(element){if(element.hasAttribute("display"))return;const{display}=getComputedStyle(element);element.setAttribute("display",display)}


const SEARCH_CONTROLS_SELECTOR='[data-apiary-widget-name="@marketplace/SearchControls"]';const CATEGORY_NAME=getCategoryName();const minReviewsFilter=new StoredInputValue(`${CATEGORY_NAME}-min-reviews-filter`,null,cleanList);const minRatingFilter=new StoredInputValue(`${CATEGORY_NAME}-min-rating-filter`,4.8,cleanList);const filterEnabled=new StoredInputValue(`${CATEGORY_NAME}-filter-enabled`,true,cleanList);function getCategoryName(){const{pathname}=window.location;const pathElements=pathname.split("/");const categoryName=pathElements[1]??"common";return categoryName}const searchControls=getFirstElement(SEARCH_CONTROLS_SELECTOR);if(searchControls){appendFilterControlsIfNeeded(searchControls,appendFiltersContainer);initProductListMods()}function initProductListMods(){cleanList();const searchResults=getFirstElement('[data-zone-name="searchResults"]');const observer=new MutationObserver(debounce(cleanList,50));observer.observe(searchResults,{childList:true,subtree:true})}function appendFiltersContainer(filterControls,parentNode){filterControls.style="display: flex;"+"gap: 15px;"+"padding: 0 10px 15px;"+"font-size: 16px;"+"font-weight: 500;";const controlStyle="display: flex;"+"align-items: center;";const inputStyle="margin: 0px 5px;";const numberInputStyle=inputStyle+// eslint-disable-line prefer-template
"border-radius: 7px;"+"border: none;"+"padding: 9px 11px;"+"box-shadow: inset 0 0 0 1.5px #d2d0cc;";const checkboxInputStyle=inputStyle+// eslint-disable-line prefer-template
"width: 23px;"+"height: 23px;";const minReviewsDiv=createMinReviewsFilterControl(minReviewsFilter,controlStyle,numberInputStyle);const minRatingDiv=createMinRatingFilterControl(minRatingFilter,controlStyle,numberInputStyle);const filterEnabledDiv=createEnabledFilterControl(filterEnabled,controlStyle,checkboxInputStyle);filterControls.append(minReviewsDiv,minRatingDiv,filterEnabledDiv);insertAfter(parentNode,filterControls)}function cleanList(){const productCards=getAllElements('[data-apiary-widget-name="@marketfront/SerpEntity"]');productCards.forEach((productCard=>{if(!filterEnabled.value){showElement(productCard);return}const productCardReviewsWrap=getFirstElement('[data-auto="reviews"]',productCard);const productCardRatingWrap=getFirstElement('[data-auto="rating"]',productCard);if(!productCardReviewsWrap||!productCardRatingWrap){hideElement(productCard);return}const productCardReviewsNumber=getElementInnerNumber(productCardReviewsWrap);const productCardRatingNumber=getElementInnerNumber(productCardRatingWrap);const conditionToHide=isLessThanFilter(productCardReviewsNumber,minReviewsFilter)||isLessThanFilter(productCardRatingNumber,minRatingFilter);showHideElement(productCard,conditionToHide)}))}})();