// ==UserScript==
// @name         Yamarket List Clean
// @description  Remove product cards by filter
// @match        https://market.yandex.ru/*
// @namespace    https://github.com/reliable-code/product-list-filters
// @version      0.5.69757140
// @icon         https://www.google.com/s2/favicons?sz=64&domain=market.yandex.ru
// @author       reliable-code
// ==/UserScript==

(()=>{"use strict";function removeNonNumber(stringValue){return stringValue.replace(/[^\d.-]/g,"")}


function getFirstElement(selector,parentNode=document,logNotFound=false){const element=parentNode.querySelector(selector);logNotFound&&!element&&console.log(`No element found for selector: ${selector}`);return element}function getAllElements(selector,parentNode=document,logNotFound=false){const elements=parentNode.querySelectorAll(selector);logNotFound&&!elements.length&&console.log(`No elements found for selector: ${selector}`);return elements}function insertAfter(existingNode,newNode){existingNode.parentNode.insertBefore(newNode,existingNode.nextSibling)}function hideElement(element){setElementDisplay(element,"none")}function showElement(element,display="block"){setElementDisplay(element,display)}function showHideElement(element,conditionToHide,display="block"){conditionToHide?hideElement(element):showElement(element,display)}function setElementDisplay(element,display){element.style.display=display}function createInput(type=null,inputOnChange=null,style=null){const input=document.createElement("input");type&&(input.type=type);inputOnChange&&input.addEventListener("change",inputOnChange);style&&(input.style=style);return input}function createNumberInput(inputOnChange,inputStyle,inputValue,inputStep,inputMinValue,inputMaxValue){const input=createInput("number",inputOnChange,inputStyle);input.value=inputValue;input.step=inputStep;input.min=inputMinValue;input.max=inputMaxValue;return input}function createCheckboxInput(inputOnChange,inputStyle,isChecked){const input=createInput("checkbox",inputOnChange,inputStyle);input.checked=isChecked;return input}function createDiv(textContent=null,style=null){const div=document.createElement("div");textContent&&(div.textContent=textContent);style&&(div.style=style);return div}function getElementInnerNumber(element,cleanText=false){let elementText=element.innerText;cleanText&&(elementText=removeNonNumber(elementText));const elementNumber=+elementText;return elementNumber}function getArrayElementInnerNumber(array,elementIndex,cleanText=false){const element=array[elementIndex];const elementNumber=getElementInnerNumber(element,cleanText);return elementNumber}


const storage=localStorage;function getStorageValueOrDefault(key,defaultValue){const localStorageItem=storage.getItem(key);return localStorageItem?JSON.parse(localStorageItem):defaultValue}function setStorageValueFromEvent(event,keyName){const{target}=event;const{type}=target;let valueToSet;if(type==="number")valueToSet=target.value;else{if(type!=="checkbox"){console.log(`Unknown input type: ${type}`);return null}valueToSet=target.checked}storage.setItem(keyName,valueToSet);return valueToSet}class StorageValue{constructor(storageKey,defaultValue){this.value=getStorageValueOrDefault(storageKey,defaultValue);this.storageKey=storageKey}updateValueFromEvent=event=>{this.value=setStorageValueFromEvent(event,this.storageKey)}}


function createFilterControlNumber(titleText,storageValue,inputStep,inputMinValue,inputMaxValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createNumberInput(storageValue.updateValueFromEvent,inputStyle,storageValue.value,inputStep,inputMinValue,inputMaxValue);filterControl.append(input);return filterControl}function createFilterControlCheckbox(titleText,storageValue,controlStyle=null,inputStyle=null){const filterControl=createDiv(titleText,controlStyle);const input=createCheckboxInput(storageValue.updateValueFromEvent,inputStyle,storageValue.value);filterControl.append(input);return filterControl}function createMinRatingFilterControl(storageValue,controlStyle=null,inputStyle=null){return createFilterControlNumber("Минимальный рейтинг: ",storageValue,"0.1","3.0","5.0",controlStyle,inputStyle)}function createMinReviewsFilterControl(storageValue,controlStyle=null,inputStyle=null){return createFilterControlNumber("Минимально отзывов: ",storageValue,"1","1","999999",controlStyle,inputStyle)}function createEnabledFilterControl(storageValue,controlStyle=null,inputStyle=null){return createFilterControlCheckbox("Вкл: ",storageValue,controlStyle,inputStyle)}function appendFilterControlsIfNeeded(parentNode,appendFiltersContainerFunc,filtersContainerId="customFiltersContainer"){let filtersContainer=getFirstElement(`#${filtersContainerId}`,parentNode);if(filtersContainer)return;filtersContainer=createDiv();filtersContainer.id=filtersContainerId;appendFiltersContainerFunc(filtersContainer,parentNode)}


const SEARCH_CONTROLS_SELECTOR='[data-apiary-widget-name="@marketplace/SearchControls"]';const VIRTUOSO_SCROLLER_SELECTOR='[data-virtuoso-scroller="true"]';const PRODUCT_CARD_SNIPPET_SELECTOR='[data-autotest-id="product-snippet"]';const PRODUCT_CARD_PARENT_ATTRIBUTE="data-apiary-widget-name";const CATEGORY_NAME=getCategoryName();const minReviewsFilter=new StorageValue(`${CATEGORY_NAME}-min-reviews-filter`,50);const minRatingFilter=new StorageValue(`${CATEGORY_NAME}-min-rating-filter`,4.8);const filterEnabled=new StorageValue(`${CATEGORY_NAME}-filter-enabled`,true);function getCategoryName(){const{pathname}=window.location;const pathElements=pathname.split("/");const categoryName=pathElements[1]??"common";return categoryName}const searchControls=getFirstElement(SEARCH_CONTROLS_SELECTOR);if(searchControls){appendFilterControlsIfNeeded(searchControls,appendFiltersContainer);setInterval(cleanList,100)}function appendFiltersContainer(filterControls,parentNode){filterControls.style="display: flex;"+"gap: 15px;"+"padding: 0 10px 15px;"+"font-size: 16px;"+"font-weight: 500;";const controlStyle="display: flex;"+"align-items: center;";const inputStyle="margin: 0px 5px;";const numberInputStyle=inputStyle+// eslint-disable-line prefer-template
"border-radius: 7px;"+"border: none;"+"padding: 9px 11px;"+"box-shadow: inset 0 0 0 1.5px #d2d0cc;";const checkboxInputStyle=inputStyle+// eslint-disable-line prefer-template
"width: 23px;"+"height: 23px;";const minReviewsDiv=createMinReviewsFilterControl(minReviewsFilter,controlStyle,numberInputStyle);const minRatingDiv=createMinRatingFilterControl(minRatingFilter,controlStyle,numberInputStyle);const filterEnabledDiv=createEnabledFilterControl(filterEnabled,controlStyle,checkboxInputStyle);filterControls.append(minReviewsDiv,minRatingDiv,filterEnabledDiv);insertAfter(parentNode,filterControls)}function cleanList(){const virtuosoScrollers=getAllElements(VIRTUOSO_SCROLLER_SELECTOR);virtuosoScrollers.forEach((virtuosoScroller=>{virtuosoScroller.style.minHeight="0"}));const productCardSnippets=getAllElements(PRODUCT_CARD_SNIPPET_SELECTOR);productCardSnippets.forEach((productCardSnippet=>{const productCardSnippetParent=productCardSnippet.parentNode;const isFirstLoad=productCardSnippetParent.hasAttribute(PRODUCT_CARD_PARENT_ATTRIBUTE);const productCard=isFirstLoad?productCardSnippetParent.parentNode.parentNode:productCardSnippetParent;if(!filterEnabled.value){showElement(productCard);return}const ratingMeter=getFirstElement('[role="meter"]',productCardSnippet);if(!ratingMeter){hideElement(productCard);return}const ratingInfoWrap=ratingMeter.parentNode;const ratingInfoSpans=getAllElements(":scope > span",ratingInfoWrap);const productCardReviewsNumber=getArrayElementInnerNumber(ratingInfoSpans,1);const productCardRatingNumber=getArrayElementInnerNumber(ratingInfoSpans,0);const conditionToHide=productCardReviewsNumber<minReviewsFilter.value||productCardRatingNumber<minRatingFilter.value;showHideElement(productCard,conditionToHide)}))}})();